CREATE FUNCTION accounting_budgets_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
declare
	TR RECORD;
begin
	IF (TG_OP='DELETE') THEN
		TR=OLD;
	ELSE
		TR=NEW;
	END IF;

	IF(TG_OP='UPDATE') THEN
		TR.modified=(select now());
	END IF;

	RETURN TR;
end;
$$;


