-- Name: advances_payments_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION advances_payments_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
  	TR RECORD;
  	VAR_AMOUNT NUMERIC;
  	FUND_ACCOUNT fund_accounts%ROWTYPE;
  	FCURRENCY currencies%ROWTYPE;
  	asientoConfirmado boolean;
  	accentryId integer;
  	exitAccount integer;
  	IDATE TIMESTAMP WITHOUT TIME ZONE;
  BEGIN
  	IF (TG_OP='DELETE') THEN
  		TR := OLD;
  	ELSE
  		TR := NEW;
  	END IF;

  	----------------------------------------------------------------
  	-- SE REGISTRA EL MOVIMIENTO EN CUENTA
  	IF (TR.fund_account_id IS NOT NULL and TR.amount > 0) THEN
  		-- DELETE FROM fund_account_transactions WHERE id IN ( SELECT fund_account_transaction_id FROM fat_purchases WHERE purchase_id = TR.id );

  		SELECT into asientoConfirmado,accentryId active,id from accentries where 
  		operation_type='advances_payments' and operation_id=TR.id;

  		IF asientoConfirmado=false OR asientoConfirmado is NULL then	--se controla que el asiento se haya confirmado

  			DELETE FROM accentries WHERE operation_type = 'advances_payments' AND operation_id = TR.id;
		
  			IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN

  				SELECT * INTO FUND_ACCOUNT FROM fund_accounts WHERE id = TR.fund_account_id;
  				SELECT * INTO FCURRENCY FROM currencies WHERE id = FUND_ACCOUNT.currency_id;
				
  				IF (TR.currency_id = FUND_ACCOUNT.currency_id) THEN
  					VAR_AMOUNT := NEW.amount;
  				ELSE
  					VAR_AMOUNT := NEW.amount * NEW.currency_price / FCURRENCY.sale_price;
  				END IF;

  				IF (DATE(NOW()) = NEW.issue_date) THEN
  					IDATE := NOW();
  				ELSE
  					IDATE := NEW.issue_date;
  				END IF;
			
  				-- SE CREA EL MOVIMIENTO EN CUENTA
  				DELETE FROM fat_advp WHERE advances_payment_id = TR.id;
  				INSERT INTO fund_account_transactions (
  					fund_account_id,
  					--curr_amount,
  					--currency_id,
  					amount,
  					issue_date,
  					appl_date,
  					method,
  					user_id
  				)
  				VALUES(
  					TR.fund_account_id,
  					--NEW.amount * -1,
  					--NEW.currency_id,
  					VAR_AMOUNT * -1,
  					IDATE,
  					IDATE,
  					'advances_payments',
  					NEW.user_id
  				);

  				INSERT INTO fat_advp (
  					advances_payment_id,
  					fund_account_transaction_id
  				)
  				VALUES (
  					NEW.id,
  					( SELECT CURRVAL('fund_account_transactions_id_seq') )
  				);
  			END IF; -- IF TG_OP = INSERT | UPDATE



  			delete from accentries where id=accentryId;
  			--se crea el nuevo asiento
  			insert into accentries("date",document_type,notes,user_id,operation_type,
  			operation_id,invoice_number,company_id) values(TR.issue_date,1,TR.notes,
  			TR.user_id,'advances_payments',TR.id,TR.invoice_number,TR.company_id);

  			SELECT into accentryId CURRVAL('accentries_id_seq');
			
  			--entra en la cuenta de fondo
  			if (TR.currency_id <> ( SELECT id FROM currencies WHERE "default" = TRUE )) THEN -- se controla si es moneda local o extranjera
  				insert into accentries_accounts(accentry_id,account_id,amount) values(
  				accentryId,(select foreign_account_id from fund_accounts where
  				id=TR.fund_account_id),TR.amount * TR.currency_price);
  			else
  				insert into accentries_accounts(accentry_id,account_id,amount) values(
  				accentryId,(select local_account_id from fund_accounts where
  				id=TR.fund_account_id),TR.amount * TR.currency_price);
  			end if;	
			
  			--sale de la cuenta del contacto
  			select into exitAccount advance_payment_account_id from contacts where
  			id=TR.contact_id; --se revisa si tiene una cuenta asociada para los anticipos

  			if exitAccount is null then
  				if TR.currency_id <> 3 then
  					select into exitAccount foreign_provider_account_id from contacts where
  					id=TR.contact_id;
  				else
  					select into exitAccount local_provider_account_id from contacts where
  					id=TR.contact_id;
  				end if;
  			end if;

  			if exitAccount is null then
  				if TR.currency_id <> 3 then
  					insert into accentries_accounts(accentry_id,account_id,amount) values(
  					accentryId,(select account_id from accounts_defaults where 
  					id=18),TR.amount*-1*TR.currency_price
  					);
  				else 
  					insert into accentries_accounts(accentry_id,account_id,amount) values(
  					accentryId,(select account_id from accounts_defaults where 
  					id=17),TR.amount*-1*TR.currency_price
  					);
  				end if;
  			else
  				insert into accentries_accounts(accentry_id,account_id,amount) values(
  				accentryId,exitAccount,TR.amount*TR.currency_price*-1
  				);
  			end if;
  		END IF;
  	END IF;
  	----------------------------------------------------------------

  	IF (TG_OP = 'DELETE') THEN
  		DELETE FROM accentries WHERE operation_type = 'advances_payments' AND operation_id = TR.id;
  	END IF;
		
  	RETURN TR;
  END
  $$;


