--
-- Name: arreglarcompras(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION arreglarcompras() RETURNS integer
    LANGUAGE plpgsql
    AS $$
declare
	cursorCompras cursor for select id from purchases where 
	payment_terms='cash' and
	fund_account_id is not null and 
	id not in(select purchase_id from purchases_fund_accounts);
	purchaseId integer;
	contador integer:=0;
begin
	
	open cursorCompras;
	fetch cursorCompras into purchaseId;

	while found loop
		contador=contador+1;
		insert into purchases_fund_accounts(fund_account_id,purchase_id,amount)
		values((select fund_account_id from purchases where id=purchaseId),
		purchaseId,
		(select amount from purchases where id=purchaseId));

		fetch cursorCompras into purchaseId;
	end loop;

	close cursorCompras;

	return contador;
end;
$$;


