--
-- Name: audit_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION audit_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	REG RECORD;
	FIELDS VARCHAR;
	NTR RECORD;
	OTR RECORD;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
		NTR := OLD;
		OTR := OLD;
	ELSE
		TR := NEW;
		IF (TG_OP='UPDATE') THEN
			NTR := NEW;
			OTR := OLD;

			IF ( TG_RELNAME = 'products' ) THEN
				IF ( NTR.modified = OTR.modified ) THEN
					RETURN TR;
				END IF;
			END IF;
			
		ELSE
			NTR := NEW;
			OTR := NEW;
		END IF;
	END IF;
	
	FIELDS := '';
	
	FOR REG IN (
		SELECT DISTINCT
		    a.attnum as num,
		    a.attname as name,
		    format_type(a.atttypid, a.atttypmod) as typ,
		    a.attnotnull as notnull, 
		    com.description as comment,
		    coalesce(i.indisprimary,false) as primary_key,
		    def.adsrc as default
		FROM pg_attribute a 
		JOIN pg_class pgc ON pgc.oid = a.attrelid
		LEFT JOIN pg_index i ON 
		    (pgc.oid = i.indrelid AND i.indkey[0] = a.attnum)
		LEFT JOIN pg_description com on 
		    (pgc.oid = com.objoid AND a.attnum = com.objsubid)
		LEFT JOIN pg_attrdef def ON 
		    (a.attrelid = def.adrelid AND a.attnum = def.adnum)
		WHERE a.attnum > 0 AND pgc.oid = a.attrelid
		AND pg_table_is_visible(pgc.oid)
		AND NOT a.attisdropped
		AND pgc.relname = TG_RELNAME
		ORDER BY a.attnum
	) LOOP
		FIELDS := FIELDS || REG.name || ',';
	END LOOP;
	
	FIELDS := '('|| TRIM(BOTH ',' FROM FIELDS) ||')';

	--IF (TG_OP='INSERT' || TG_OP='DELETE') THEN
		INSERT INTO audits (
			event_code,
			user_id,
			table_name,
			register_id,
			field_name,
			new_data,
			old_data,
			field_names
		)
		VALUES (
			TG_OP,
			TR.user_id,
			TG_RELNAME,
			TR.id,
			NULL,
			NTR::text,
			OTR::text,
			FIELDS
		);
	--ELSE
		
	--END IF;
	
	RETURN TR;
END
$$;


