--
-- Name: budgets_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION budgets_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$declare 
						    TR RECORD;
						    _BUDGETS REFCURSOR;
						    _PROJECT_CURRENCY RECORD;
						    _PROJECT RECORD;
						    _TOTAL NUMERIC;
						    _ROW RECORD;
						    _BUDGET_CURRENCY RECORD;
						begin
						    IF (TG_OP='DELETE') THEN
						        TR := OLD;
						    ELSE
						        TR := NEW;
						    END IF;

						    IF(TG_OP<>'DELETE') THEN
						    IF(TR.project_id IS NOT NULL AND TR.work_budget = TRUE AND TR.master_budget = TRUE) THEN
						      UPDATE projects SET status = TR.status WHERE id = TR.project_id AND status NOT IN ('done','approved');
						    END IF;
						  END IF;

						  IF (TR.project_id IS NOT NULL) THEN
						    _TOTAL := 0;
						    SELECT * INTO _PROJECT FROM projects WHERE id = TR.project_id;
						    SELECT * INTO _PROJECT_CURRENCY FROM currencies WHERE id = (SELECT currency_id FROM projects WHERE id = TR.project_id);
						    OPEN _BUDGETS FOR SELECT * FROM budgets WHERE project_id = TR.project_id;
						    LOOP
						      FETCH _BUDGETS INTO _ROW;
						      EXIT WHEN NOT FOUND;
						      SELECT * INTO _BUDGET_CURRENCY FROM currencies WHERE id = _ROW.currency_id;
						      IF(_ROW.currency_id <> _PROJECT_CURRENCY.id) THEN
						        _TOTAL := _TOTAL + ( _ROW.amount * _BUDGET_CURRENCY.buy_price / _PROJECT_CURRENCY.buy_price );
						      ELSE
						        _TOTAL := _TOTAL + ( _ROW.amount );
						      END IF;
						    END LOOP;
						    RAISE NOTICE 'TOTAL %', _TOTAL;
						    UPDATE
						      projects
						    SET
						      price = _TOTAL
						    WHERE
						      projects.id = TR.project_id
						    ;
						  END IF;

						  RETURN TR;
						end;
						$$;


