--
-- Name: budgets_before(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION budgets_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$declare
						    TR RECORD;
						    _COUNT INTEGER;
						begin
						    IF (TG_OP='DELETE') THEN
						        TR := OLD;
						    ELSE
						        TR := NEW;
						    END IF;

						    IF(TG_OP<>'DELETE') THEN
						      -- Si es presupuesto maestro, evitar modificarlo
						      IF(TR.project_id IS NOT NULL AND TR.master_budget = false) THEN
						        -- Al registrar un presupuesto adicional, si es el unico del proyecto, es un presupuesto maestro
						        -- Si existen otros  presupuestos adicionales, es un presupuesto adicional
						        SELECT count(id) INTO _COUNT FROM budgets WHERE project_id = TR.project_id;
						        IF _COUNT > 0 THEN
