CREATE FUNCTION collections_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
  	TR RECORD;
  	CURRENCY currencies%ROWTYPE;
  	CURRENCY_PRICE currencies_prices%ROWTYPE;
  BEGIN
  	IF (TG_OP='DELETE') THEN
  		TR := OLD;
  	ELSE
  		TR := NEW;
  	END IF;

  	-- SE DEFINE LA MONEDA SEGUN LA MONEDA DE LA VENTA DEL PAGARE
  	IF (TG_OP='INSERT') THEN
  		SELECT * INTO CURRENCY_PRICE FROM currencies_prices WHERE TR.issue_date BETWEEN currencies_prices.init_date AND currencies_prices.end_date AND currencies_prices.currency_id = TR.currency_id ORDER by id DESC LIMIT 1;
  		SELECT * INTO CURRENCY FROM currencies WHERE currencies.id = TR.currency_id;

  		IF ( CURRENCY_PRICE.id IS NOT NULL ) THEN
  			TR.currency_price := CURRENCY_PRICE.buy_price;
  			TR.legal_currency_price := CURRENCY_PRICE.legal_buy_price;
  		ELSE
  			SELECT currencies.buy_price INTO TR.currency_price FROM currencies WHERE currencies.id = TR.currency_id;
  			SELECT currencies.buy_price INTO TR.local_currency_price FROM currencies WHERE currencies."local" = true;
  			SELECT currencies.legal_buy_price INTO TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
  			SELECT currencies.legal_buy_price INTO TR.legal_local_currency_price FROM currencies WHERE currencies."local" = true;
  		END IF;

  	END IF;

  	IF (TG_OP='INSERT' and TR.online=true) THEN
  		TR.doc_id=TR.id;
  	END IF;

  	IF (TG_OP <> 'DELETE') THEN
  		IF (TR.journal_id IS NULL) THEN
  			SELECT
  				journals.id INTO TR.journal_id
  			FROM
  				journals
  			WHERE
  				journals.posale_id = TR.posale_id
  			AND
  				journals.open_tm <= TR.created
  			AND
  				( journals.close_tm IS NULL OR journals.close_tm >= TR.created )
  			;
  		END IF;
  	END IF;
	
  	RETURN TR;
  END
  $$;


