CREATE FUNCTION collections_payment_methods_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
	      DECLARE
	          TR RECORD;
	          PM payment_methods%ROWTYPE;
	          COLLECTION collections%ROWTYPE;
	          PNOTE BIGINT;
	          FA BIGINT;
	          DTD INT;
	          APPLDATE TIMESTAMP WITHOUT TIME ZONE;
	      BEGIN
	          IF (TG_OP='DELETE') THEN
	              TR := OLD;
	          ELSE
	              TR := NEW;
	          END IF;

	          SELECT * INTO COLLECTION FROM collections WHERE id = TR.collection_id;

	          SELECT sales_pnote_id INTO PNOTE FROM collections WHERE collections.id = TR.collection_id;

	          -- SE ACTUALIZA EL TOTAL PAGADO DEL PAGARE RELACIONADO
	          PERFORM set_sales_pnotes_paid_total(PNOTE);

	          IF (COLLECTION.journal_id IS NOT NULL) THEN
	              SELECT created + interval '1 second' INTO APPLDATE FROM journals WHERE id = COLLECTION.journal_id;
	          ELSE
	              IF (COLLECTION.issue_date IS NULL OR COLLECTION.issue_date = DATE(COLLECTION.created)) THEN
	                  APPLDATE := COLLECTION.created;
	              ELSE
	                  APPLDATE := (COLLECTION.issue_date::varchar || ' 10:00:00')::timestamp;
	              END IF;
	          END IF;

	          DELETE FROM fat_collections WHERE collection_id = COLLECTION.id AND ( collections_payment_method_id = TR.id OR collections_payment_method_id IS NULL );
    
	          ----------------------------------------------------------------
	          -- SE REGISTRA EL MOVIMIENTO EN CUENTA
	          IF (TG_OP='INSERT' OR TG_OP='UPDATE') THEN
	              -- SE OBTIENEN LOS DATOS DE LA FORMA DE PAGO
	              SELECT * INTO PM FROM payment_methods WHERE id = NEW.payment_method_id;

	              RAISE NOTICE 'PM=%', PM.name;
	              RAISE NOTICE 'PM.expense=%', PM.expense;

	              IF (NEW.payment_method_id IS NOT NULL AND PM.expense = FALSE) THEN

	                  RAISE NOTICE 'PM.expense=false';
	                  RAISE NOTICE 'APPLDATE=%', APPLDATE;
            
	                  IF (PM.fund_account_id IS NULL) THEN
	                      SELECT fund_account_id INTO FA FROM posales WHERE posales.id = COLLECTION.posale_id;
	                      DTD = 0;
	                  ELSE
	                      FA = PM.fund_account_id;
	                      DTD = PM.days_to_deposit;
	                  END IF;
                
	                  -- SE CREA EL MOVIMIENTO EN CUENTA
	                  INSERT INTO fund_account_transactions (
	                      fund_account_id,
	                      curr_amount,
	                      currency_id,
	                      issue_date,
	                      appl_date,
	                      method,
	                      user_id
	                  )
	                  VALUES(
	                      FA,
	                      NEW.amount,
	                      COLLECTION.currency_id,
	                      APPLDATE,
	                      APPLDATE,
	                      'collection',
	                      COLLECTION.user_id
	                  );

	                  INSERT INTO fat_collections (
	                      collection_id,
	                      fund_account_transaction_id,
	                      collections_payment_method_id
	                  )
	                  VALUES (
	                      COLLECTION.id,
	                      ( SELECT CURRVAL('fund_account_transactions_id_seq') ),
	                      TR.id
	                  );

	                  IF (TR.check_number IS NOT NULL AND TR.check_number <> '') THEN

	                      INSERT INTO received_checks (
	                          check_number,
	                          bank_id,
	                          collections_payment_method_id,
	                          method,
	                          pay_date,
	                          collection_id,
	                          amount,
	                          currency_id,
	                          company_id,
	                          contact_id,
	                          auth_user_id,
	                          user_id,
	                          notes
	                      )
	                      SELECT  p.check_number,
	                          p.bank_id, p.id,
	                          'method', 
	                          COALESCE(p.pay_date,('now'::text)::date), 
	                          p.collection_id, 
	                          p.amount, 
	                          p.currency_id, 
	                          c.company_id, 
	                          c.contact_id, 
	                          c.user_id, 
	                          user_id, 
	                          c.notes 
	                      FROM
	                          collections_payment_methods as p
	                          LEFT JOIN collections as c
	                      ON p.collection_id = c.id
	                          WHERE p.id = TR.id
	                      ; 
                                    
	                  END IF;
                
	              ELSE
            
	                  IF ( NEW.sale_credit_note_id IS NOT NULL ) THEN
	                      PERFORM set_sale_credit_notes_discount_applied(NEW.sale_credit_note_id);
	                  END IF; 

	              END IF;
        
	          ELSE
        
	              IF ( OLD.sale_credit_note_id IS NOT NULL ) THEN
	                  PERFORM set_sale_credit_notes_discount_applied(OLD.sale_credit_note_id);
	              END IF; 
            
	          END IF;
	      ----------------------------------------------------------------
    
	      -- SE ACTUALIZA EL TOTAL DEL COBRO
	      UPDATE
	          collections
	      SET
	          total_paid = ( SELECT SUM(amount * currency_price) FROM collections_payment_methods WHERE collection_id = collections.id ) / collections.currency_price
	      WHERE
	          collections.id = TR.collection_id
	      ;
        
	      RETURN TR;
	  END
	  $$;


