-- Name: collections_prolease_disbursements_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION collections_prolease_disbursements_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
DECLARE
	TR RECORD;
	_PNOTES REFCURSOR;
	_ROW RECORD;
	_COLLECTION RECORD;
	_DISBURSEMENT RECORD;
	_CONTRACT RECORD;
	_FIRST BOOLEAN;
	_AMOUNT NUMERIC;
	_DAYS DOUBLE PRECISION;
	_TS DOUBLE PRECISION;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;
		SELECT * INTO _COLLECTION FROM collections WHERE id = TR.collection_id;
		SELECT * INTO _DISBURSEMENT FROM prolease_disbursements WHERE id = TR.prolease_disbursement_id;
		SELECT * INTO _CONTRACT FROM prolease_contracts WHERE id = _DISBURSEMENT.prolease_contract_id;
		_FIRST := TRUE;
		OPEN _PNOTES FOR SELECT * FROM prolease_disbursement_pnotes WHERE prolease_disbursement_id = TR.prolease_disbursement_id AND amount > paid AND expiration_date > _COLLECTION.issue_date AND yearly_commission = false ORDER by expiration_date ASC;
		LOOP
			FETCH _PNOTES INTO _ROW;
			EXIT WHEN NOT FOUND;
			IF(_FIRST) THEN
				_DAYS := DATE_PART('day', _ROW.expiration_date) - DATE_PART('day', _COLLECTION.issue_date);
				_AMOUNT := ((_DISBURSEMENT.amount * (_CONTRACT.interest_rate/100))/360)*_DAYS;
				UPDATE prolease_disbursement_pnotes SET amount = _AMOUNT WHERE id = _ROW.id;
				_FIRST := FALSE;
			ELSE
				UPDATE prolease_disbursement_pnotes SET paid = amount WHERE id = _ROW.id;
			END IF;
		END LOOP;	

	PERFORM set_prolease_disbursements_paid(TR.prolease_disbursement_id);
	RETURN TR;
END
$$;


