-- Name: delivery_notes_products_before_insert_update(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION delivery_notes_products_before_insert_update() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TAX taxes%ROWTYPE;
	PRODUCT products%ROWTYPE;
	CURRENCY currencies%ROWTYPE;
	PRODUCT_CURRENCY currencies%ROWTYPE;
	DELIVERY_NOTE delivery_notes%ROWTYPE;
	DC_PRICE NUMERIC;
	PTYPE ptypes%ROWTYPE;
	AUXNUM NUMERIC;
	CCOST NUMERIC;
	V_PRICING_ID BIGINT;
BEGIN
	IF (NEW.aum_id is not NULL) THEN -- se agrega el factor de conversion
		SELECT aums.quantity into NEW.aum_factor from aums where aums.id=NEW.aum_id;
	END IF;
	SELECT * INTO DELIVERY_NOTE FROM delivery_notes WHERE delivery_notes.id = NEW.delivery_note_id;
	SELECT * INTO CURRENCY FROM currencies WHERE id = DELIVERY_NOTE.currency_id;
	
	SELECT products.* INTO PRODUCT FROM products WHERE products.id = NEW.product_id;
	SELECT * INTO PRODUCT_CURRENCY FROM currencies WHERE id = PRODUCT.sell_price_currency_id;
	
	-- DETERMINACION DE PRECIO
	IF (NEW.price IS NULL OR NEW.price = 0) THEN
		IF ( NEW.pricing_id IS NOT NULL ) THEN
			DC_PRICE := COALESCE(( SELECT amount FROM products_pricings WHERE product_id = NEW.product_id AND pricing_id = NEW.pricing_id LIMIT 1 ), NEW.price, 0);
		ELSE
			DC_PRICE := PRODUCT.sell_price * PRODUCT_CURRENCY.buy_price;
		END IF;
		NEW.price := DC_PRICE / DELIVERY_NOTE.currency_price;
		NEW.price := (select round(NEW.price,CURRENCY.decimals));
	END IF;
	NEW.currency_id := DELIVERY_NOTE.currency_id;

	-- IF (NEW.discount_amount > 0) THEN
	--	NEW.discount_amount := NEW.discount_amount;
	--	NEW.discount_rate := NEW.discount_amount * 100 / NEW.price;
	--	NEW.price := NEW.price - (NEW.discount_amount / NEW.quantity);
	--	NEW.price := (select round(NEW.price,CURRENCY.decimals));
	-- ELSIF (NEW.discount_rate > 0) THEN
	--	NEW.discount_amount := NEW.discount_rate * NEW.price / 100;
	--	NEW.price := NEW.price - NEW.discount_amount;
	--	NEW.price := (select round(NEW.price,CURRENCY.decimals));
	-- END IF;
	-- FIN DETERMINACION DE PRECIO

	-- DETERMINACION DE IMPUESTOS
	IF ( DELIVERY_NOTE.billed = TRUE ) THEN
		-- SE DETERMINA EL IMPUESTO DE VENTA
		SELECT taxes.* INTO TAX FROM taxes WHERE id IN ( SELECT tax_id FROM products_taxes WHERE product_id = NEW.product_id LIMIT 1 );
		NEW.tax_id := TAX.id;
		NEW.tax_rate := COALESCE(TAX.rate, 0);
		IF (TAX.rate IS NOT NULL) THEN
			NEW.tax_amount := ((NEW.price * (NEW.quantity - NEW.total_tourism_scheme)) * (PRODUCT.taxable / 100)) * TAX.rate / (100 + TAX.rate);
		ELSE
			NEW.tax_amount := 0;
		END IF;
	END IF;
	-- FIN DETERMINACION DE IMPUESTOS


	-- DETERMINACION DEL COSTO
	IF(TG_OP='INSERT') THEN
		NEW.cost := PRODUCT.buy_price + PRODUCT.total_expenses + PRODUCT.total_direct_expenses;
		IF ( PRODUCT.composers > 0 ) THEN
			SELECT get_product_composition_cost( PRODUCT.ID ) INTO CCOST;
			NEW.cost := NEW.cost + CCOST;
		END IF;
		NEW.cost_currency_id := PRODUCT.buy_price_currency_id;
		SELECT currencies.* INTO PRODUCT_CURRENCY FROM currencies WHERE currencies.id = PRODUCT.buy_price_currency_id;
		NEW.cost_currency_buy_price := PRODUCT_CURRENCY.buy_price;
		NEW.cost_currency_legal_buy_price := PRODUCT_CURRENCY.legal_buy_price;
	END IF;
	-- FIN DETERMINACION DEL COSTO


	RETURN NEW;
END
$$;


