--
-- Name: fat_before(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION fat_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	FUND_ACCOUNT fund_accounts%ROWTYPE;
	AUXBI BIGINT;
	FA_CURRENCY currencies%ROWTYPE;
	CURRENCY currencies%ROWTYPE;
	AUXREAL REAL;
	FAJOURNAL fajournals%ROWTYPE;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;
	SELECT * INTO FUND_ACCOUNT FROM fund_accounts WHERE id = TR.fund_account_id;
	IF (TG_OP = 'INSERT') THEN
		IF ( TR.fajournal_id IS NULL AND FUND_ACCOUNT.imprest AND FUND_ACCOUNT.current_fajournal_id IS NOT NULL ) THEN
			SELECT * INTO FAJOURNAL FROM fajournals WHERE id = FUND_ACCOUNT.current_fajournal_id;
			IF (DATE(TR.appl_date) = DATE(FAJOURNAL.init_time)  AND TR.appl_date < FAJOURNAL.init_time) THEN
				TR.appl_date = FAJOURNAL.init_time + INTERVAL '1 second';
			END IF;
			IF (DATE(TR.issue_date) = DATE(FAJOURNAL.init_time) AND TR.issue_date < FAJOURNAL.init_time) THEN
				TR.issue_date = FAJOURNAL.init_time + INTERVAL '1 second';
			END IF;
			IF ( TR.appl_date >= FAJOURNAL.init_time AND ( TR.appl_date <= FAJOURNAL.end_time OR FAJOURNAL.end_time IS NULL ) ) THEN
				TR.fajournal_id = FAJOURNAL.id;
			END IF;
		END IF;

		IF ( FUND_ACCOUNT.imprest AND TR.fajournal_id IS NULL ) THEN
  			SELECT * INTO FAJOURNAL FROM fajournals WHERE fajournals.fund_account_id = FUND_ACCOUNT.id AND fajournals.init_time <= TR.appl_date AND ( fajournals.end_time >= TR.appl_date OR fajournals.end_time IS NULL);
  			RAISE NOTICE 'AA';
  			IF (FAJOURNAL.id IS NOT NULL) THEN
  				RAISE NOTICE 'AA 1';
  				TR.fajournal_id = FAJOURNAL.id;
  			ELSE
  				RAISE NOTICE 'AA 2';
  			END IF;
  		END IF;
	END IF;
	IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN
		IF ((TR.amount IS NULL OR TR.amount = 0) AND (TR.curr_amount IS NOT NULL AND TR.curr_amount <> 0 AND TR.currency_id IS NOT NULL)) THEN
			-- SI NO SE INDICO EL MONTO EN LA MONEDA DE LA CUENTA
			-- PERO SI EN OTRA MONEDA, ENTONCES SE CALCULA EL MONTO
			-- EN LA MONEDA DE LA CUENTA.
			-- Primero se verifica si las cuentas coinciden
			SELECT currencies.* INTO FA_CURRENCY FROM fund_accounts, currencies WHERE fund_accounts.currency_id = currencies.id AND fund_accounts.id = TR.fund_account_id;
			IF (FA_CURRENCY.id = TR.currency_id) THEN
				-- Si las cuentas coinciden, entonces
				TR.amount := TR.curr_amount;
			ELSE
				-- Si las cuentas no coinciden, entonces
				-- se pasa curr_amount a la moneda principal
				SELECT * INTO CURRENCY FROM currencies WHERE currencies.id = TR.currency_id;
				IF (CURRENCY.default) THEN
					AUXREAL := TR.curr_amount;
				ELSE
					-- Si la moneda NO es la moneda principal, se convierte a la principal
					AUXREAL := TR.curr_amount * CURRENCY.buy_price;
				END IF;
				-- Se cambia el monto a la moneda de la cuenta
				TR.amount := AUXREAL / FA_CURRENCY.buy_price;
			END IF;
			
		END IF;
	END IF;

	RETURN TR;
END
$$;


