--
-- Name: fund_transfers_before(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION fund_transfers_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
  	TR RECORD;
  	OUT_FAT BIGINT;
  	IN_FAT BIGINT;
  	APPLDATE TIMESTAMP WITHOUT TIME ZONE;
  	SRC_CURRENCY currencies%ROWTYPE;
  	DST_CURRENCY currencies%ROWTYPE;
  	SRC_AMOUNT NUMERIC;
  	DST_AMOUNT NUMERIC;
  	AUTO_CONFIRM BOOLEAN;
  BEGIN
  	IF (TG_OP='DELETE') THEN
  		TR := OLD;
  	ELSE
  		TR := NEW;
  	END IF;

	SET session_replication_role = replica;
  	IF (TR.out_fat_id IS NOT NULL) THEN
  		DELETE FROM fund_account_transactions WHERE id = TR.out_fat_id;
  	END IF;

  	IF (TR.in_fat_id IS NOT NULL) THEN
  		DELETE FROM fund_account_transactions WHERE id = TR.in_fat_id;
  	END IF;
  	SET session_replication_role = DEFAULT;
	
  	IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN

		SELECT active_fund_transfers INTO AUTO_CONFIRM FROM companies WHERE companies.id = TR.company_id;

		IF ( AUTO_CONFIRM = TRUE AND TR.status <> 'accepted' ) THEN
			TR.status := 'accepted';
			-- NEW.status := 'accepted';
		END IF;
  	
  		IF ( NEW.void = FALSE AND TR.status = 'accepted') THEN
			
  			IF (DATE(TR.created) = TR.issue_date) THEN
  				APPLDATE := TR.created;
  			ELSE
  				APPLDATE := TR.issue_date;
  			END IF;

  			SELECT * INTO SRC_CURRENCY FROM currencies WHERE id = ( SELECT fund_accounts.currency_id FROM fund_accounts WHERE fund_accounts.id = TR.src_fund_account_id );
  			SELECT * INTO DST_CURRENCY FROM currencies WHERE id = ( SELECT fund_accounts.currency_id FROM fund_accounts WHERE fund_accounts.id = TR.dst_fund_account_id );

  			IF ( SRC_CURRENCY.id = DST_CURRENCY.id ) THEN
  				IF ( SRC_CURRENCY.id = TR.currency_id ) THEN
  					SRC_AMOUNT := TR.amount;
  					DST_AMOUNT := TR.amount;
  				ELSE
  					SRC_AMOUNT := TR.amount * TR.currency_price / SRC_CURRENCY.sale_price;
  					DST_AMOUNT := TR.amount * TR.currency_price / DST_CURRENCY.sale_price;
  				END IF;
  			ELSE
  				IF ( SRC_CURRENCY.id = TR.currency_id ) THEN
  					SRC_AMOUNT := TR.amount;
  				ELSE
  					SRC_AMOUNT := TR.amount * TR.currency_price / SRC_CURRENCY.sale_price;
  				END IF;

  				IF ( DST_CURRENCY.id = TR.currency_id ) THEN
  					DST_AMOUNT := TR.amount;
  				ELSE
  					DST_AMOUNT := TR.amount * TR.currency_price / DST_CURRENCY.sale_price;
  				END IF;
  			END IF;
			
  			-- Extraccion
  			INSERT INTO fund_account_transactions (
  				fund_account_id,
  				issue_date,
  				appl_date,
  				method,
  				notes,
  				user_id,
  				amount,
  				"code"
  			)
  			VALUES (
  				TR.src_fund_account_id,
  				APPLDATE,
  				APPLDATE,
  				'swift',
  				TR.notes,
  				TR.user_id,
  				SRC_AMOUNT * -1,
  				TR.code
  			);
  			SELECT CURRVAL('fund_account_transactions_id_seq') INTO TR.out_fat_id;
  			-- Deposito
  			INSERT INTO fund_account_transactions (
  				fund_account_id,
  				issue_date,
  				appl_date,
  				method,
  				notes,
  				user_id,
  				amount,
  				"code"
  			)
  			VALUES (
  				TR.dst_fund_account_id,
  				APPLDATE,
  				APPLDATE,
  				'swift',
  				TR.notes,
  				TR.user_id,
  				DST_AMOUNT,
  				TR.code
  			);
  			SELECT CURRVAL('fund_account_transactions_id_seq') INTO TR.in_fat_id;
  		END IF;
  	END IF;
		
  	RETURN TR;
  END
  $$;


