CREATE FUNCTION "getCommission"(_id integer) RETURNS numeric
    LANGUAGE plpgsql
    AS $$DECLARE  
									    _ROW RECORD;
									    _APPOINTMENT RECORD;
									    _PRODUCT RECORD;
									    _AMOUNT NUMERIC;
									    _PERCE INTEGER;
									    _DAYOFFS INTEGER;
									    _ISSUNDAY BOOLEAN;
									    _METHOD VARCHAR;
									  BEGIN
									    SELECT * INTO _ROW FROM appointments_products WHERE id = _ID;
									    SELECT * INTO _APPOINTMENT FROM appointments WHERE id = _ROW.appointment_id;
									    SELECT * INTO _PRODUCT FROM products WHERE id = _ROW.product_id;
									    SELECT INTO _ISSUNDAY CASE WHEN extract(dow from _APPOINTMENT.issue_date::timestamp) = 0 THEN true ELSE false END;
									    SELECT COUNT(id) INTO _DAYOFFS FROM professionals_dayoffs

									    WHERE professional_id = _ROW.professional_id
									    AND "date" = _APPOINTMENT.issue_date
									    AND start_time BETWEEN _ROW.start_time::varchar AND _ROW.end_time::varchar
									    OR "date" = _APPOINTMENT.issue_date
									    AND _ROW.start_time::varchar BETWEEN start_time AND end_time AND professional_id = _ROW.professional_id;

									    IF _ISSUNDAY THEN
									      _DAYOFFS := 1;
									    END IF;

									    IF(_DAYOFFS<1) THEN
									      SELECT COALESCE(commission,0) INTO _PERCE FROM professionals_products_times WHERE professional_id = _ROW.professional_id AND product_id = _ROW.product_id AND commission > 0;
									        _METHOD := _ISSUNDAY || ' professionals_products_times';
									      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      IF(_PERCE<1) THEN
									        SELECT COALESCE(commission_rate,0) INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id AND commission_rate > 0;
									        _METHOD := _ISSUNDAY || ' ptypes_professionals_commissions';
									        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      END IF;
									      IF(_PERCE<1) THEN
									        SELECT COALESCE(commision_rate,0) INTO _PERCE FROM professionals WHERE id = _ROW.professional_id AND commision_rate > 0;
									        _METHOD := _ISSUNDAY || ' professionals';
									        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      END IF;
									      IF(_PERCE<1) THEN
									        SELECT COALESCE(professional_commission,0) INTO _PERCE FROM products WHERE id = _ROW.product_id AND professional_commission > 0;
									        _METHOD := _ISSUNDAY || ' products';
									        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      END IF;
									    ELSE
									      SELECT COALESCE(extra_commission,0) INTO _PERCE FROM professionals_products_times WHERE professional_id = _ROW.professional_id AND product_id = _ROW.product_id AND extra_commission > 0;
									        _METHOD := _ISSUNDAY || ' professionals_products_times';
									      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      IF(_PERCE<1) THEN
									        SELECT COALESCE(dayoff_commission_rate,0) INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id AND dayoff_commission_rate > 0;
									        _METHOD := _ISSUNDAY || ' ptypes_professionals_commissions';
									        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      END IF;
									      IF(_PERCE<1) THEN
									        SELECT COALESCE(dayoff_commision_rate,0) INTO _PERCE FROM professionals WHERE id = _ROW.professional_id AND dayoff_commision_rate > 0;
									        _METHOD := _ISSUNDAY || ' professionals';
									        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      END IF;
									      IF(_PERCE<1) THEN
									        SELECT COALESCE(professional_commission,0) INTO _PERCE FROM products WHERE id = _ROW.product_id AND professional_commission > 0;
									        _METHOD := _ISSUNDAY || ' products';
									        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
									      END IF;
									    END IF;
									    RETURN (_PERCE*(_ROW.price*_ROW.quantity))/100;
									  END
									  $$;


