CREATE FUNCTION "getCommissionPerc"(_id integer) RETURNS numeric
    LANGUAGE plpgsql
    AS $$DECLARE
  _ROW RECORD;
  _APPOINTMENT RECORD;
  _PRODUCT RECORD;
  _PROFESSIONAL RECORD;
  _AMOUNT NUMERIC;
  _PERCE INTEGER;
  _DAYOFFS INTEGER;
  _SPECIALDAYOFFS INTEGER;
  _ISSUNDAY BOOLEAN;
BEGIN
  SELECT * INTO _ROW FROM appointments_products WHERE id = _ID;
  SELECT * INTO _APPOINTMENT FROM appointments WHERE id = _ROW.appointment_id;
  SELECT * INTO _PRODUCT FROM products WHERE id = _ROW.product_id;
  SELECT * INTO _PROFESSIONAL FROM professionals WHERE id = _ROW.professional_id;
  SELECT INTO _ISSUNDAY CASE WHEN extract(dow from _APPOINTMENT.issue_date::timestamp) IN (0,1) THEN true ELSE false END;
  RAISE NOTICE '%', _ISSUNDAY;
  
  SELECT COUNT(id) INTO _SPECIALDAYOFFS FROM free_days WHERE "date" = _APPOINTMENT.issue_date::date;
  
  SELECT COUNT(id) INTO _DAYOFFS FROM professionals_dayoffs
    WHERE professional_id = _ROW.professional_id
    AND "date" = _APPOINTMENT.issue_date
    AND start_time BETWEEN _ROW.start_time::varchar AND _ROW.end_time::varchar
    OR "date" = _APPOINTMENT.issue_date
    AND _ROW.start_time::varchar BETWEEN start_time AND end_time AND professional_id = _ROW.professional_id;

  IF(_DAYOFFS<1) THEN
        SELECT COALESCE(commission_rate,0) INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id;
        RAISE NOTICE 'ptypes_professionals_commissions %', _PERCE;
        IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    IF(_PERCE<1) THEN
            SELECT COALESCE(commission,0) INTO _PERCE FROM professionals_products_times WHERE professional_id = _ROW.professional_id AND product_id = _ROW.product_id;
            RAISE NOTICE 'professionals_products_times %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT COALESCE(commission_rate,0) INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id;
      RAISE NOTICE 'ptypes_professionals_commissions %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT COALESCE(commision_rate,0) INTO _PERCE FROM professionals WHERE id = _ROW.professional_id AND commision_rate > 0;
      RAISE NOTICE 'professionals %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT CASE WHEN _PROFESSIONAL.parttime IS TRUE THEN COALESCE(part_comission,0) ELSE COALESCE(professional_commission,0) END INTO _PERCE FROM products WHERE id = _ROW.product_id;
      RAISE NOTICE 'products %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
  ELSE
        SELECT COALESCE(dayoff_commission_rate,0) INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id;
        RAISE NOTICE 'ptypes_professionals_commissions %', _PERCE;
    IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    IF(_PERCE<1) THEN
            SELECT COALESCE(extra_commission,0) INTO _PERCE FROM professionals_products_times WHERE professional_id = _ROW.professional_id AND product_id = _ROW.product_id;
            RAISE NOTICE 'professionals_products_times %', _PERCE;
            IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
        END IF;
    IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    IF(_PERCE<1) THEN
      SELECT CASE WHEN _PROFESSIONAL.parttime IS TRUE THEN COALESCE(dayoff_part_comission,0) ELSE COALESCE(dayoff_commission_rate,0) END INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id;
      RAISE NOTICE 'ptypes_professionals_commissions %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT COALESCE(dayoff_commision_rate,0) INTO _PERCE FROM professionals WHERE id = _ROW.professional_id;
      RAISE NOTICE 'professionals %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT CASE WHEN _PROFESSIONAL.parttime IS TRUE THEN COALESCE(dayoff_part_comission,0) ELSE COALESCE(professional_commission,0) END INTO _PERCE FROM products WHERE id = _ROW.product_id;
      RAISE NOTICE 'products %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
  END IF;
  -- Si es feriado / dia libre aplicar la comision especial
  IF(_SPECIALDAYOFFS>0 OR _ISSUNDAY) THEN
        RAISE NOTICE 'SUNDAY';
        SELECT COALESCE(special_commission,0) INTO _PERCE FROM ptypes_professionals_commissions WHERE professional_id = _ROW.professional_id;
    RAISE NOTICE 'ptypes_professionals_commissions %', _PERCE;
    IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    IF(_PERCE<1) THEN
            SELECT COALESCE(special_commission,0) INTO _PERCE FROM professionals_products_times WHERE professional_id = _ROW.professional_id AND product_id = _ROW.product_id;
      RAISE NOTICE 'professionals_products_times %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT COALESCE(special_commission,0) INTO _PERCE FROM professionals WHERE id = _ROW.professional_id;
      RAISE NOTICE 'professionals %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
    IF(_PERCE<1) THEN
      SELECT CASE WHEN _PROFESSIONAL.parttime IS TRUE THEN COALESCE(part_special_commission,0) ELSE COALESCE(special_commission,0) END  INTO _PERCE FROM products WHERE id = _ROW.product_id;
      RAISE NOTICE 'products %', _PERCE;
      IF ( _PERCE IS NULL ) THEN _PERCE:=0; END IF;
    END IF;
  END IF;
  RETURN _PERCE;
END
$$;


