-- Name: getFinancingRule(bigint, character varying); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION "getFinancingRule"(pnote_id bigint, method character varying) RETURNS numeric
    LANGUAGE plpgsql
    AS $$DECLARE
  _VAL NUMERIC;
  _RATE NUMERIC;
  _GLOAN RECORD;
  _GLOAN_PNOTE RECORD;
  _DAYS INTEGER;
BEGIN
  --Routine body goes here...
  SELECT * INTO _GLOAN_PNOTE FROM gloans_pnotes WHERE id = pnote_id;
  SELECT * INTO _GLOAN FROM gloans WHERE id = _GLOAN_PNOTE.gloan_id;

  SELECT 
    CASE WHEN DATE_PART('day', NOW()-expiration_date) > 0 THEN
      DATE_PART('day', NOW()-expiration_date)
    ELSE
      0
    END
    INTO _DAYS FROM gloans_pnotes WHERE id = pnote_id;

  IF "method" <> 'moratory' THEN
    EXECUTE 'SELECT ' || "method" || ' FROM financing_rules
        WHERE 
          company_id = ' || _GLOAN.company_id || '
        AND
          (
            (
              '|| _DAYS ||' BETWEEN "from" AND "to"
              AND "from" is not null
              AND "to" is not null
            )
            OR
            (
              '|| _DAYS ||' >= "from" AND
              ( "to" is NULL OR "to" = 0 )
            )
          ) LIMIT 1' INTO _RATE;
      RAISE NOTICE 'SELECT % FROM financing_rules
        WHERE 
          company_id = %
        AND
          (
            (
              % BETWEEN "from" AND "to"
              AND "from" is not null
              AND "to" is not null
            )
            OR
            (
              % >= "from" AND
              ( "to" is NULL OR "to" = 0 )
            )
          ) LIMIT 1', "method", _GLOAN.company_id, _DAYS, _DAYS;

      RAISE NOTICE '% % %', _GLOAN.company_id, _DAYS, _RATE;
      IF "method" = 'punitory_rate' THEN 
        _VAL := ( ((_GLOAN.moratory_interest/100) * _GLOAN_PNOTE.amount) / 30 ) * _DAYS;
        _VAL := _VAL * _RATE / 100; 
      ELSEIF "method" = 'expense_rate' THEN 
        _VAL := _GLOAN_PNOTE.amount * _RATE / 100; 
      END IF; 
  ELSE
    _VAL := ( ((_GLOAN.moratory_interest/100) * _GLOAN_PNOTE.amount) / 30 ) * _DAYS;
  END IF; 

  IF(_DAYS>0) THEN
    RETURN COALESCE(_VAL,0);
  ELSE
    RETURN 0;
  END IF;
END
$$;


