--

CREATE FUNCTION get_product_composition_cost(arg_product bigint) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
DECLARE
	CCOST NUMERIC;
BEGIN	
	SELECT
		SUM( ROUND( composer.buy_price * currencies.buy_price * product_components.quantity / products.production_base , cp.decimals ) ) INTO CCOST
	FROM
		products,
		product_components,
		products as composer,
		currencies,
		currencies AS cp
	WHERE
		products.id = arg_product
	AND
		products.id = product_components.product_id
	AND
		product_components.component_product_id = composer.id
	AND
		composer.buy_price_currency_id = currencies.id
	AND
		products.sell_price_currency_id = cp.id
	;
	RETURN COALESCE(CCOST, 0);
END
$$;


