--
-- Name: get_product_composition_cost(bigint, bigint); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION get_product_composition_cost(arg_product bigint, profundidad bigint) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
DECLARE
	CCOST NUMERIC DEFAULT 0;
	PRODQ NUMERIC DEFAULT 1;
BEGIN
	RAISE NOTICE 'get_product_composition_cost profundidad=%', profundidad;
	IF (profundidad > 15) THEN
		RETURN 0;
	END IF;
	SELECT production_base INTO PRODQ FROM products WHERE id = arg_product;
	SELECT
		SUM(
			(
				products.buy_price * currencies.buy_price +
				get_product_composition_cost(products.id, profundidad + 1)
			) * product_components.quantity
		) INTO CCOST
	FROM
		product_components,
		products,
		currencies
	WHERE
		product_components.product_id = arg_product
	AND
		component_product_id = products.id
	AND
		buy_price_currency_id = currencies.id	
	;
	RAISE NOTICE 'SE RETORNA: % / %', COALESCE(CCOST, 0), PRODQ;
	RETURN COALESCE(CCOST, 0) / PRODQ;
END;
$$;


