CREATE FUNCTION gloans_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  TR RECORD;
  APPLDATE TIMESTAMP WITHOUT TIME ZONE;
BEGIN
  IF (TG_OP='DELETE') THEN
    TR := OLD;
  ELSE
    TR := NEW;
  END IF;

  DELETE FROM fat_gloans WHERE gloan_id = TR.id;

  IF (TG_OP <> 'DELETE') THEN
    INSERT INTO fund_account_transactions (
      fund_account_id,
      curr_amount,
      currency_id,
      issue_date,
      appl_date,
      method,
      user_id,
      notes
    )
    VALUES(
      NEW.fund_account_id,
      (NEW.amount) * -1,
      NEW.currency_id,
      COALESCE(NEW.disbursement_date,NEW.issue_date),
      COALESCE(NEW.disbursement_date,NEW.issue_date),
      'gloan',
      NEW.user_id,
      NEW.notes
    );

    INSERT INTO fat_gloans (
      gloan_id,
      fund_account_transaction_id
    )
    VALUES (
      NEW.id,
      ( SELECT CURRVAL('fund_account_transactions_id_seq') )
    );
  END IF;
    
    
  RETURN TR;
END
$$;


