CREATE FUNCTION gloans_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	VAMOUNT NUMERIC;
	TAXRATE NUMERIC;
	CURRENCY currencies%ROWTYPE;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;

	IF (TG_OP='UPDATE' OR TG_OP='INSERT') THEN

		-- PROCESAR IMPUESTO
		SELECT * INTO CURRENCY FROM currencies WHERE id = TR.currency_id;
		IF ( TR.tax_id IS NOT NULL ) THEN
			SELECT rate INTO TAXRATE FROM taxes WHERE id = TR.tax_id;
		ELSE
			TAXRATE := 0;
		END IF;
		
		TR.tax_amount := ROUND(TAXRATE * TR.interest / ( 100 + TAXRATE ), CURRENCY.decimals);
		-- FIN PROCESAR IMPUESTO
		
	END IF;
		
	RETURN TR;
END
$$;


