CREATE FUNCTION incons_products_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	V_AMOUNT NUMERIC;
	V_QTY NUMERIC;
	V_TOTAL_SOLD NUMERIC;
	V_TOTAL_RETURNED NUMERIC;
	PRODUCT products%ROWTYPE;
	INCON incons%ROWTYPE;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;

	SELECT
		COALESCE( SUM(price * quantity), 0 ),
		COALESCE( SUM(quantity), 0 ),
		COALESCE( SUM(total_sold), 0 ),
		COALESCE( SUM(total_returned), 0 )
	INTO
		V_AMOUNT,
		V_QTY,
		V_TOTAL_SOLD,
		V_TOTAL_RETURNED
	FROM
		incons_products
	WHERE
		incons_products.incon_id = TR.incon_id
	;

	UPDATE
		incons
	SET
		amount = V_AMOUNT,
		total_items = V_QTY,
		total_sold = V_TOTAL_SOLD,
		total_returned = V_TOTAL_RETURNED
	WHERE
		incons.id = TR.incon_id
	;

	-- SE GENERAN MOVIMIENTOS DE STOCK ---------------------

	DELETE FROM stocks WHERE incons_product_id = TR.id;

	IF ( TG_OP <> 'DELETE' ) THEN

		SELECT * INTO PRODUCT FROM products WHERE products.id = TR.product_id;
		SELECT * INTO INCON FROM incons WHERE incons.id = TR.incon_id;

		INSERT INTO stocks (
			product_id,
			total_sent,
			price,
			currency_id,
			warehouse_id,
			total,
			total2,
			received,
			status,
			incons_product_id,
			total_tourism_scheme,
			accounting_cost,
			"method"
		)
		VALUES (
			PRODUCT.id,
			TR.quantity,
			PRODUCT.buy_price,
			PRODUCT.buy_price_currency_id,
			INCON.warehouse_id,
			TR.quantity,
			TR.quantity,
			INCON.created,
			'delivered',
			TR.id,
			0,
			PRODUCT.buy_price_untaxed,
			'incon'
		);

		IF ( TR.total_returned > 0 ) THEN
			INSERT INTO stocks (
				product_id,
				total_sent,
				price,
				currency_id,
				warehouse_id,
				total,
				total2,
				received,
				status,
				incons_product_id,
				total_tourism_scheme,
				accounting_cost,
				"method"
			)
			VALUES (
				PRODUCT.id,
				TR.total_returned * -1,
				PRODUCT.buy_price,
				PRODUCT.buy_price_currency_id,
				INCON.warehouse_id,
				TR.quantity,
				TR.quantity,
				INCON.created,
				'delivered',
				TR.id,
				0,
				PRODUCT.buy_price_untaxed,
				'incon'
			);
		END IF;
	END IF;

	--------------------------------------------------------
	
	RETURN TR;
END
$$;


