							  	  	REC RECORD;
							  	  	QUANTITY NUMERIC;
							  	  	PRODUCT products%ROWTYPE;
							  	  	CURR BOOLEAN;
							  	  	_DELTA NUMERIC;
							  	  	PRODUCT_COST NUMERIC;
							  	  BEGIN
							  	  	IF (TG_OP='DELETE') THEN
							  	  		TR := OLD;
							  	  	ELSE
							  	  		TR := NEW;
							  	  	END IF;

							  	  	----------------------------------------------------------------
							  	  	-- ACTUALIZACION DEL INVENTARIO
							  	  	IF (TG_OP='UPDATE') THEN
							  	  		IF (OLD.status = 'pending' AND NEW.status = 'confirmed') THEN
							  	  			IF (TR.authorized_on IS NULL) THEN
							  	  				TR.authorized_on := CURRENT_TIMESTAMP;
							  	  			END IF;
							  	  			IF (date_trunc('minute', TR.authorized_on) = date_trunc('minute', NOW())) THEN
							  	  				-- SI SE ACABA DE ACTUALIZAR, ENTONCES SE TOMA LA DISPONIBILIDAD ACTUAL
							  	  				CURR := TRUE;
							  	  			ELSE
							  	  				-- SI ES UN AJUSTE ANTIGUO, ENTONCES SE CALCULA LA DISPONIBILIDAD A LA FECHA
							  	  				CURR := FALSE;
							  	  			END IF;
							  	  			FOR REC IN ( SELECT * FROM invads_products WHERE invad_id = NEW.id ) LOOP
							  	  				IF (CURR = TRUE) THEN
							  	  					SELECT "total" INTO QUANTITY FROM availabilities WHERE product_id = REC.product_id AND warehouse_id = NEW.warehouse_id;
							  	  				ELSE
							  	  					SELECT SUM("total") INTO QUANTITY FROM stocks WHERE product_id = REC.product_id AND warehouse_id = NEW.warehouse_id AND received < TR.authorized_on AND status IN ('delivered', 'received');
							  	  				END IF;

							  	  				QUANTITY := COALESCE(QUANTITY, 0);

							  	  				RAISE NOTICE 'QUANTITY=%', QUANTITY;
							  	  				RAISE NOTICE 'DELTA=%', TR.delta;

							  	  				IF (TR.delta = FALSE) THEN
							  	  					UPDATE invads_products SET expected_quantity = QUANTITY WHERE id = REC.id;
							  	  					QUANTITY := REC.reported_quantity - QUANTITY;
							  	  				ELSE
							  	  					_DELTA := QUANTITY - REC.expected_quantity;
							  	  					RAISE NOTICE 'DELTA=%', _DELTA;
							  	  					UPDATE invads_products SET expected_quantity = QUANTITY, reported_quantity = reported_quantity + _DELTA, delta = _DELTA WHERE id = REC.id;
							  	  					QUANTITY := REC.reported_quantity + _DELTA - QUANTITY;
							  	  				END IF;

							  	  				RAISE NOTICE 'EXPECTED QUANTITY=%', REC.expected_quantity;
							  	  				RAISE NOTICE 'REPORTED QUANTITY=%', REC.reported_quantity;
							  	  				RAISE NOTICE 'NEW QUANTITY=%', QUANTITY;
				
							  	  				SELECT * INTO PRODUCT FROM products WHERE id = REC.product_id;

							  	  				DELETE FROM stocks WHERE invads_product_id = REC.id;
							  	  				IF (PRODUCT.composers > 0) THEN
							  	  					PRODUCT_COST := get_product_composition_cost_untaxed(PRODUCT.id, 0);
							  	  				ELSE
							  	  					PRODUCT_COST := PRODUCT.buy_price_untaxed;
							  	  				END IF;
							  	  				IF (PRODUCT.stockable) THEN
							  	  					INSERT INTO stocks (
							  	  						product_id,
							  	  						total,
							  	  						price,
							  	  						currency_id,
							  	  						warehouse_id,
							  	  						user_id,
							  	  						total_sent,
							  	  						received,
							  	  						status,
							  	  						method,
							  	  						invads_product_id,
							  	  						serial,
							  	  						accounting_cost
							  	  					)
							  	  					VALUES (
							  	  						PRODUCT.id,
							  	  						QUANTITY,
							  	  						REC.buy_price,
							  	  						PRODUCT.buy_price_currency_id,
							  	  						NEW.warehouse_id,
							  	  						NEW.user_id,
							  	  						QUANTITY,
							  	  						TR.authorized_on,
							  	  						'delivered',
							  	  						'adjustement',
							  	  						REC.id,
							  	  						REC.serial,
							  	  						REC.buy_price_untaxed
							  	  					);
							  	  				END IF;
				
							  	  				-- SE EJECUTA ESTE UPDATE PARA QUE SE INSERTEN LOS SERIALES EN pserials_trans
							  	  				-- UPDATE invads_products_serials SET id = id WHERE invads_product_id = REC.id;
							  	  				INSERT INTO stocks_serials (
							  	  					stock_id,
							  	  					code,
							  	  					created
							  	  				)
							  	  				SELECT
							  	  					CURRVAL('stocks_id_seq'),
							  	  					code,
							  	  					TR.authorized_on
							  	  				FROM
							  	  					invads_products_serials
							  	  				WHERE
							  	  					invads_product_id = REC.id
							  	  				;
							  	  			END LOOP;
							  	  		END IF;
							  	  	END IF;
							  	  	----------------------------------------------------------------

							  	  	--IF (TR.delta = TRUE) THEN
							  	  	--	UPDATE invads SET delta = FALSE WHERE id = TR.id;
							  	  	--END IF;
		
							  	  	RETURN TR;
							  	  END
							  	  $$;


