CREATE FUNCTION invtransferences_products_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
	    DECLARE
	      TR RECORD;
	    _TOTAL_QUANTITY NUMERIC;
	    _TOTAL_RCV_QUANTITY NUMERIC;
	    _TOTAL_PRICE NUMERIC;
	    _TOTAL_COST NUMERIC;
	  	_TOTAL_COST_UNTAXED NUMERIC;
	    _WAREHOUSE_ID INTEGER;
	    _PROJECT_ID INTEGER;
	    _FE BOOLEAN;
	    _TOTAL_COST_RRHH NUMERIC;
	    _TOTAL_COST_FUEL NUMERIC;
	    _CURRENCY RECORD;
	    _PROJECT_CURRENCY RECORD;
	    _TOTAL NUMERIC;
	    BEGIN
	      IF (TG_OP='DELETE') THEN
	        TR := OLD;
	      ELSE
	        TR := NEW;
	      END IF;

	    SELECT
	      SUM(quantity),
	      SUM(rcv_quantity * quantity),
	      SUM(sale_price * quantity),
	      SUM(buy_price * quantity),
	  		SUM(buy_price_untaxed * quantity)
	    INTO
	      _TOTAL_QUANTITY,
	      _TOTAL_RCV_QUANTITY,
	      _TOTAL_PRICE,
	      _TOTAL_COST,
	  		_TOTAL_COST_UNTAXED
	    FROM
	      invtransferences_products
	    WHERE
	      invtransference_id = TR.invtransference_id
	    ;
  
	    UPDATE
	      invtransferences
	    SET
	      total_price = _TOTAL_PRICE,
	      total_cost = _TOTAL_COST,
	  		total_cost_untaxed = _TOTAL_COST_UNTAXED,
	      total_quantity = _TOTAL_QUANTITY,
	      total_rcv_quantity = _TOTAL_RCV_QUANTITY
	    WHERE
	      id = TR.invtransference_id
	    ;

	      -- Feature Exclusivo para Innobra
	      SELECT feature('innobra') INTO _FE;

	      IF _FE THEN
	        -- Traer almacen destino
	        SELECT dst_warehouse_id INTO _WAREHOUSE_ID FROM invtransferences WHERE id = TR.invtransference_id;

	        -- Adquirir costo total de transferencias de un almacen
	        SELECT COALESCE(SUM(total_cost),0) INTO _TOTAL_COST FROM invtransferences WHERE dst_warehouse_id = _WAREHOUSE_ID;

	        -- Adquirir ID del projecto
	        SELECT project_id INTO _PROJECT_ID FROM projects_warehouses WHERE warehouse_id = _WAREHOUSE_ID;

	        -- Sumamos costos RRHH
	        SELECT COALESCE(SUM(cost),0) INTO _TOTAL_COST_RRHH FROM precence_records WHERE project_id = _PROJECT_ID;

	        -- Sumamos costos COMBUSTIBLE
	        SELECT COALESCE(SUM(cost),0) INTO _TOTAL_COST_FUEL FROM projects_fuels_orders WHERE project_id = _PROJECT_ID;

	        SELECT * INTO _PROJECT_CURRENCY FROM currencies WHERE id = (SELECT currency_id FROM projects WHERE id = _PROJECT_ID);
	        SELECT * INTO _CURRENCY FROM currencies WHERE "default" = true;
	        _TOTAL := _TOTAL_COST+_TOTAL_COST_RRHH+_TOTAL_COST_FUEL;
	        SELECT change_currency(_CURRENCY.id,_PROJECT_CURRENCY.id,_TOTAL) INTO _TOTAL;

	        -- Si es nulo, puede tratarse de deposito de almacen principal u otros.
	        IF _PROJECT_ID IS NOT NULL THEN
	          UPDATE projects SET total_expenses = _TOTAL WHERE id = _PROJECT_ID;
	        END IF; 
      
	      END IF;

	      RETURN TR;
	    END
	    $$;


