CREATE FUNCTION issues_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE	
	TR RECORD;
	IPHASE iphases%ROWTYPE;
	TARGET_ISSUE_ID BIGINT;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;

	IF (TG_OP = 'INSERT') THEN
		INSERT INTO itransitions (
			issue_id,
			iphase_id,
			entry,
			user_id
		)
		VALUES (
			TR.id,
			TR.iphase_id,
			NOW(),
			TR.user_id
		);
	ELSE
		IF ( TG_OP = 'UPDATE' ) THEN
			IF ( NEW.iphase_id <> OLD.iphase_id ) THEN
				UPDATE itransitions SET "exit" = NOW() WHERE issue_id = NEW.id AND "exit" IS NULL;
				INSERT INTO itransitions (
					issue_id,
					iphase_id,
					entry,
					user_id
				)
				VALUES (
					TR.id,
					TR.iphase_id,
					NOW(),
					TR.user_id
				);
			END IF;

			IF ( NEW.service_center_id IS NOT NULL AND NEW.service_center_id <> OLD.service_center_id ) THEN
				UPDATE issues_products SET id = id WHERE issue_id = NEW.id;
			END IF;
		END IF;
	END IF;

	IF (TR.project_id IS NOT NULL) THEN
		
		UPDATE
			projects
		SET
			total_issues = COALESCE((
				SELECT
					COUNT(*)
				FROM
					issues
				WHERE
					void = false
				AND
					project_id = projects.id
			), 0),
			total_issues_done = COALESCE((
				SELECT
					COUNT(*)
				FROM
					issues
				WHERE
					void = false
				AND
					project_id = projects.id
				AND
					closed IS NOT NULL
			), 0),
			total_points = COALESCE((
				SELECT
					SUM(difficulty)
				FROM
					issues
				WHERE
					void = false
				AND
					project_id = projects.id
			), 0),
			total_points_done = COALESCE((
				SELECT
					SUM(issues.difficulty)
				FROM
					issues
				WHERE
					void = false
				AND
					project_id = projects.id
				AND
					closed IS NOT NULL
			), 0)
		WHERE
			projects.id = TR.project_id
		;
		
	END IF;

	IF (TR.sprint_id IS NOT NULL) THEN
		
		UPDATE
			sprints
		SET
			total_issues = COALESCE((
				SELECT
					COUNT(*)
				FROM
					issues
				WHERE
					void = false
				AND
					sprint_id = sprints.id
			), 0),
			total_issues_done = COALESCE((
				SELECT
					COUNT(*)
				FROM
					issues
				WHERE
					void = false
				AND
					sprint_id = sprints.id
				AND
					closed IS NOT NULL
			), 0),
			total_points = COALESCE((
				SELECT
					SUM(difficulty)
				FROM
					issues
				WHERE
					void = false
				AND
					sprint_id = sprints.id
			), 0),
			total_points_done = COALESCE((
				SELECT
					SUM(difficulty)
				FROM
					issues
				WHERE
					void = false
				AND
					sprint_id = sprints.id
				AND
					closed IS NOT NULL
			), 0)
		WHERE
			sprints.id = TR.sprint_id
		;
		
	END IF;

	IF ( TG_OP = 'INSERT' OR TG_OP = 'UPDATE' ) THEN 
		SELECT * INTO IPHASE FROM iphases WHERE iphases.id = NEW.iphase_id;
		IF ( IPHASE.linked_iphase_id IS NOT NULL ) THEN
			SELECT id INTO TARGET_ISSUE_ID FROM issues WHERE parent_issue_id = TR.id AND src_iphase_id = TR.iphase_id;
			IF ( TARGET_ISSUE_ID IS NULL ) THEN
				INSERT INTO issues (
					name,
					contact_id,
					user_id,
					itype_id,
					pipeline_id,
					iphase_id,
					contact_email,
					priority,
					leader_email,
					difficulty,
					parent_issue_id,
					src_iphase_id
				)
				VALUES (
					TR.name,
					TR.contact_id,
					TR.user_id,
					TR.itype_id,
					( SELECT iphases.pipeline_id FROM iphases WHERE iphases.id = IPHASE.linked_iphase_id ),
					IPHASE.linked_iphase_id,
					TR.contact_email,
					TR.priority,
					TR.leader_email,
					TR.difficulty,
					TR.id,
					TR.iphase_id
				);
				INSERT INTO inotes (
					"issue_id",
					-- "method"
					"notes"
				)
				SELECT
					( SELECT MAX(id) FROM issues ),
					--"method",
					"notes"
				FROM
					inotes
				WHERE
					issue_id = TR.id
				ORDER BY
					inotes.id
				LIMIT
					1
				;
			END IF;
		END IF;
	END IF;

	RETURN TR;
END
$$;


