-- Name: issues_products_update_stock(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION issues_products_update_stock() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	SERVICE_CENTER service_centers%ROWTYPE;
	PRODUCT products%ROWTYPE;
	TOTAL_SERIALS INTEGER;
BEGIN
	RAISE NOTICE 'sales_products_update_stock';
	IF (TG_OP='DELETE') THEN
		DELETE FROM stocks WHERE issues_product_id = OLD.id;
		RETURN OLD;
	ELSE

		SELECT
			service_centers.*
		INTO
			SERVICE_CENTER
		FROM
			service_centers,
			issues
		WHERE
			issues.id = NEW.issue_id
		AND
			issues.service_center_id = service_centers.id
		;

		IF ( SERVICE_CENTER.wowarehouse_id IS NULL ) THEN
			RAISE NOTICE 'No se ha definido un deposito. Se aborta.';
			RETURN NEW;
		END IF;	
		
		IF (TG_OP='UPDATE') THEN
			IF (NEW.quantity = OLD.quantity) THEN
				-- RAISE NOTICE 'No ha cambiado la cantidad. Se aborta.';
				-- RETURN NEW;
			END IF;
		END IF;
		
		SELECT * INTO PRODUCT FROM products WHERE id = NEW.product_id;

		IF ( PRODUCT.stockable = FALSE AND PRODUCT.composers = 0 ) THEN
			RAISE NOTICE 'El producto no es inventariable.';
			RETURN NEW;
		END IF;

		DELETE FROM stocks WHERE issues_product_id = NEW.id;

		SELECT COUNT(*) INTO TOTAL_SERIALS FROM issues_products_serials WHERE issues_product_id = NEW.id;

		IF ( NEW.quantity > TOTAL_SERIALS ) THEN
			INSERT INTO stocks (
				product_id,
				total_sent,
				price,
				currency_id,
				warehouse_id,
				total,
				received,
				status,
				issues_product_id,
				total_tourism_scheme,
				"method"
			)
			VALUES (
				PRODUCT.id,
				ABS(NEW.quantity - TOTAL_SERIALS) * -1,
				PRODUCT.buy_price,
				PRODUCT.buy_price_currency_id,
				SERVICE_CENTER.wowarehouse_id,
				ABS(NEW.quantity - TOTAL_SERIALS) * -1,
				NEW.created,
				'delivered',
				NEW.id,
				0,
				'issue'
			);
		END IF;

		IF ( TOTAL_SERIALS > 0 ) THEN
			-- HAY SERIALES
			INSERT INTO stocks (
				product_id,
				total_sent,
				price,
				currency_id,
				warehouse_id,
				total,
				received,
				status,
				issues_product_id,
				total_tourism_scheme,
				"method",
				"serial"
			)
			SELECT
				PRODUCT.id,
				-1,
				PRODUCT.buy_price,
				PRODUCT.buy_price_currency_id,
				SERVICE_CENTER.wowarehouse_id,
				-1,
				NEW.created,
				'delivered',
				NEW.id,
				0,
				'issue',
				issues_products_serials."code"
			FROM
				issues_products_serials
			WHERE
				issues_products_serials.issues_product_id = NEW.id
			;
		END IF;
		
		RETURN NEW;
	END IF;
END
$$;


