CREATE FUNCTION loans_deposits_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	VAMOUNT NUMERIC;
	TAXRATE NUMERIC;
	CURRENCY currencies%ROWTYPE;
	LOAN loans%ROWTYPE;
	LOANS_PNOTE_ID BIGINT;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;

	PERFORM set_loan_paid(TR.loan_id); 

	DELETE FROM fat_loans_deposits WHERE loans_deposit_id = TR.id;

	IF (TG_OP='UPDATE' OR TG_OP='INSERT') THEN

		SELECT * INTO LOAN FROM loans WHERE loans.id = TR.loan_id;

		INSERT INTO fund_account_transactions (
			fund_account_id,
			issue_date,
			appl_date,
			method,
			user_id,
			curr_amount,
			currency_id
		)
		VALUES (
			LOAN.fund_account_id,
			NEW.date,
			NEW.date,
			'loan_deposit',
			LOAN.user_id,
			NEW.amount,
			LOAN.currency_id
		);

		INSERT INTO fat_loans_deposits (
			fund_account_transaction_id,
			loans_deposit_id
		)
		SELECT
			MAX(id),
			NEW.id
		FROM
			fund_account_transactions
		;

		IF ( LOAN.type = 'factoring' OR LOAN.type = 'credit_card' ) THEN
			SELECT id INTO LOANS_PNOTE_ID FROM loans_pnotes WHERE loans_deposit_id = TR.id;
			IF ( LOANS_PNOTE_ID IS NULL ) THEN
				INSERT INTO loans_pnotes (
					loan_id,
					loans_deposit_id,
					amount,
					expiration_date
				)
				VALUES (
					TR.loan_id,
					TR.id,
					TR.amount,
					TR.date
				);
			ELSE
				UPDATE
					loans_pnotes
				SET
					amount = TR.amount,
					expiration_date = TR.date
				WHERE
					loans_pnotes.id = LOANS_PNOTE_ID
				;
			END IF;
		END IF;

	END IF;
	
	UPDATE
		loans
	SET
		deposited = COALESCE(( SELECT SUM(amount) FROM loans_deposits WHERE loan_id = TR.loan_id ), 0)
	WHERE
		id = TR.loan_id
	;
	
	RETURN TR;
END
$$;


