--
-- Name: pa_calcular_dv_11_a(character varying, numeric); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION pa_calcular_dv_11_a(p_numero character varying, p_basemax numeric) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
declare
  v_total         numeric(6):=0;
  v_resto         numeric(2);
  k               numeric(2);
  v_numero_aux    numeric(1);
  v_numero_al     varchar(255):='';
  v_caracter      varchar(1);
  v_digit         numeric;
BEGIN
  FOR i IN 1 .. LENGTH(p_numero) LOOP
    v_caracter  := UPPER(SUBSTR(p_numero,i,1));
    IF ASCII(v_caracter) NOT BETWEEN 48 AND 57 THEN  -- de 0 a 9 
       v_numero_al := v_numero_al || ASCII(v_caracter);
  ELSE
     v_numero_al := v_numero_al || v_caracter;
    END IF;
  
  END LOOP;
  k           := 2;
  v_total     := 0;
  FOR i IN REVERSE LENGTH(v_numero_al) .. 1  LOOP
    IF k > p_basemax THEN
      k := 2;
    END IF;
    v_numero_aux := cast(SUBSTR(v_numero_al,i,1) as numeric);
    v_total      := v_total + (v_numero_aux * k);
    k            := k + 1;
  END LOOP;

  v_resto := MOD(v_total,11);

  IF v_resto > 1 THEN
    v_digit := 11 - v_resto;
  ELSE
    v_Digit := 0;
  END IF;

  RETURN v_Digit;

END;
$$;


