CREATE FUNCTION payments_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TR RECORD;
	FACHANGED BOOLEAN;
	CURRENCY currencies%ROWTYPE;
	CURRENCY_PRICE currencies_prices%ROWTYPE;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
		FACHANGED := FALSE;
	ELSE
		TR := NEW;
		IF ( TG_OP = 'INSERT' ) THEN
			FACHANGED := TRUE;
		ELSE
			IF ( TG_OP = 'UPDATE' ) THEN
				RAISE NOTICE 'ITS AN UPDATE';
				IF ( NEW.currency_id <> OLD.currency_id OR ( OLD.currency_id IS NULL AND NEW.currency_id IS NOT NULL ) ) THEN
					FACHANGED := TRUE;
					RAISE NOTICE 'CURRENCY_ID CHANGED';
				ELSE
					FACHANGED := FALSE;
					RAISE NOTICE 'CURRENCY_ID NOT CHANGED';
				END IF;
			END IF;
		END IF;
	END IF;

	RAISE NOTICE 'FACHANGED=%', FACHANGED;


	-- SE DEFINE LA MONEDA SEGUN LA CUENTA DE FONDOS	
	IF (FACHANGED = TRUE AND TR.currency_id IS NOT NULL) THEN

		SELECT * INTO CURRENCY_PRICE FROM currencies_prices WHERE TR.requested_on BETWEEN currencies_prices.init_date AND currencies_prices.end_date AND currencies_prices.currency_id = TR.currency_id ORDER by id DESC LIMIT 1;
		SELECT * INTO CURRENCY FROM currencies WHERE currencies.id = TR.currency_id;

		IF ( CURRENCY_PRICE.id IS NOT NULL ) THEN
			TR.legal_currency_price := CURRENCY_PRICE.legal_sale_price;
			TR.currency_price := CURRENCY_PRICE.sale_price;
		ELSE
			SELECT currencies.sale_price INTO TR.currency_price FROM currencies WHERE currencies.id = TR.currency_id;
			SELECT currencies.legal_sale_price INTO TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
			SELECT currencies.legal_sale_price INTO TR.legal_local_currency_price FROM currencies WHERE currencies.local = TRUE;
		END IF;
		-- SELECT currency_id INTO TR.currency_id FROM fund_accounts WHERE id = TR.fund_account_id;
	END IF;
	
	RETURN TR;
END
$$;


