--
-- Name: payments_fund_accounts_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION payments_fund_accounts_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
  	PM payments%ROWTYPE;
  	TR RECORD;
  	APPLDATE TIMESTAMP WITHOUT TIME ZONE;
  	FA_CURRENCY currencies%ROWTYPE;
  	PM_CURRENCY currencies%ROWTYPE;
  	AMOUNT NUMERIC;
  BEGIN
  	IF (TG_OP='DELETE') THEN
  		TR := OLD;
  	ELSE
  		TR := NEW;
  	END IF;
	
  	----------------------------------------------------------------
  	-- SE ELIMINAN LOS MOVIMIENTOS ANTERIORES
  	DELETE FROM fat_payments_fund_accounts WHERE payments_fund_account_id = TR.id;

  	SELECT * INTO PM FROM payments WHERE id = TR.payment_id;

  	SELECT
		currencies.*
	INTO
		FA_CURRENCY
	FROM
		currencies,
		fund_accounts
	WHERE
		fund_accounts.id = TR.fund_account_id
	AND
		fund_accounts.currency_id = currencies.id
	;

	AMOUNT := TR.amount;

	IF ( PM.currency_id <> FA_CURRENCY.id ) THEN
		--SELECT
		--	*
		--INTO
		--	PM_CURRENCY
		--FROM
		--	currencies
		--WHERE
		--	currencies.id = PM.currency_id
		--;

		AMOUNT := ROUND(AMOUNT * PM.currency_price / FA_CURRENCY.sale_price, FA_CURRENCY.decimals);
	END IF;

	

  	IF ((TG_OP='INSERT' OR TG_OP='UPDATE') AND PM.status = 'approved') THEN
		IF (TR.fund_account_id IS NOT NULL) THEN
			APPLDATE := COALESCE(NEW.pay_date, NEW.issue_date, PM.authorized_on, PM.requested_on);
			INSERT INTO fund_account_transactions (
  				fund_account_id,
  				amount,
  				currency_id,
  				issue_date,
  				appl_date,
  				method,
  				user_id,
  				created,
  				code,
  				contact_id,
  				notes
			)
			VALUES(
				NEW.fund_account_id,
				AMOUNT * -1,
				PM.currency_id,
				APPLDATE,
				APPLDATE, -- REEMPLAZAR POR EL CALCULO
				'payment',
				COALESCE(PM.authorized_by, PM.requested_by),
  				APPLDATE,
  				TR.check_number,
  				PM.contact_id,
  				'PAYMENT #'||PM.id
			);

			INSERT INTO fat_payments_fund_accounts (
				payments_fund_account_id,
				fund_account_transaction_id
			)
			VALUES (
				NEW.id,
				( SELECT CURRVAL('fund_account_transactions_id_seq') )
			);

			IF (TR.check_number IS NOT NULL AND TR.check_number <> '') THEN
				UPDATE
					checkbooks
				SET
					latest_number_used = TR.check_number::integer
				WHERE
					checkbooks.enabled = TRUE
				AND
					checkbooks.fund_account_id = TR.fund_account_id
				AND
					( checkbooks.latest_number_used IS NULL OR checkbooks.latest_number_used < TR.check_number::integer )
				AND
					checkbooks.end_number >= TR.check_number::integer
				;
			END IF;
		END IF;
  	END IF;

  	RETURN TR;
  END
  $$;


