-- Name: porders_products_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION porders_products_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$DECLARE 
	    	TR RECORD;
	    	PRODUCT products%ROWTYPE;
	    	PORDER porders%ROWTYPE;
	    	CEP ceps%ROWTYPE;

	    BEGIN
	    	IF (TG_OP='DELETE') THEN
	    		TR := OLD;
	    	ELSE
	    		TR := NEW;
	    	END IF;

	    	IF (TG_OP <> 'DELETE') THEN

	    		IF ( TR.produced > 0 ) THEN

	    			SELECT * INTO PRODUCT FROM products WHERE id = TR.product_id;
	    			SELECT * INTO PORDER FROM porders WHERE id = TR.porder_id;
	    			SELECT * INTO CEP FROM ceps WHERE id = PORDER.cep_id;

			
	    			-- SE DESCUENTAN LOS INSUMOS DE STOCK
	    			IF (PORDER.production_line) THEN
	    				RAISE NOTICE 'PRODUCTION LINE'; 
	    			ELSE
	    				DELETE FROM stocks WHERE porders_product_id = TR.id;
	    				INSERT INTO stocks (
	    					product_id,
	    					total,
	    					price,
	    					accounting_cost,
	    					currency_id,
	    					warehouse_id,
	    					user_id,
	    					total_sent,
	    					received,
	    					status,
	    					method,
	    					porders_product_id,
	    					sales_product_id
	    				)
	    				SELECT
	    					component_product_id,
	    					quantity / PRODUCT.production_base * -1 * TR.produced,
	    					products.buy_price,
	    					CASE WHEN products.composers > 0 THEN
	    						get_product_composition_cost_untaxed(products.id, 0)
	    					ELSE
	    						products.buy_price_untaxed
	    					END,
	    					products.buy_price_currency_id,
	    					COALESCE(PORDER.source_warehouse_id, CEP.warehouse_id),
	    					PORDER.user_id,
	    					quantity / PRODUCT.production_base * -1 * TR.produced,
	    					NOW(),
	    					'delivered',
	    					'production',
	    					TR.id,
	    					PORDER.sales_product_id
	    				FROM
	    					product_components,
	    					products
	    				WHERE
	    					product_components.product_id = TR.product_id
	    				AND
	    					products.id = component_product_id
	    				AND
	    					products.stockable = TRUE
	    				;
	    			END IF;
	
	    			IF (PORDER.production_line) THEN
	    				RAISE NOTICE 'PRODUCTION LINE'; 
	    			ELSE
	    				-- SE INTRODUCE EL PRODUCTO EN STOCK
	    				IF (PRODUCT.stockable) THEN
	    					INSERT INTO stocks (
	    						product_id,
	    						total,
	    						price,
	    						accounting_cost,
	    						currency_id,
	    						warehouse_id,
	    						user_id,
	    						total_sent,
	    						received,
	    						status,
	    						method,
	    						porders_product_id,
	    						sales_product_id
	    					)
	    					VALUES (
	    						TR.product_id,
	    						TR.produced,
	    						PRODUCT.buy_price,
	    						CASE WHEN PRODUCT.composers > 0 THEN
	    							get_product_composition_cost_untaxed(PRODUCT.id, 0)
	    						ELSE
	    							PRODUCT.buy_price_untaxed
	    						END,
	    						PRODUCT.buy_price_currency_id,
	    						COALESCE(PORDER.destination_warehouse_id, CEP.warehouse_destination_id),
	    						PORDER.user_id,
	    						TR.produced,
	    						NOW(),
	    						'delivered',
	    						'production',
	    						TR.id,
	    						PORDER.sales_product_id
	    					);

	    					-- SI LA ORDEN DE PRODUCCION FUE GENERADA A PARTIR DE UNA VENTA
	    					-- SE VUELVE A DESCONTAR EL ITEM INTRODUCIDO AL STOCK
	    					IF (PORDER.sales_product_id IS NOT NULL) THEN
	    						INSERT INTO stocks (
	    							product_id,
	    							total,
	    							price,
	    							accounting_cost,
	    							currency_id,
	    							warehouse_id,
	    							user_id,
	    							total_sent,
	    							received,
	    							status,
	    							method,
	    							porders_product_id,
	    							sales_product_id
	    						)
	    						VALUES (
	    							TR.product_id,
	    							TR.produced*-1,
	    							PRODUCT.buy_price,
	    							CASE WHEN PRODUCT.composers > 0 THEN
	    								get_product_composition_cost_untaxed(PRODUCT.id, 0)
	    							ELSE
	    								PRODUCT.buy_price_untaxed
	    							END,
	    							PRODUCT.buy_price_currency_id,
	    							COALESCE(PORDER.destination_warehouse_id, CEP.warehouse_id),
	    							PORDER.user_id,
	    							TR.produced*-1,
	    							NOW(),
	    							'delivered',
	    							'production',
	    							TR.id,
	    							PORDER.sales_product_id
	    						);
	    					END IF; -- IF PORDER.SALES_PRODUCT_ID
	    				END IF;
	    			END IF;
	    		END IF;
	    	END IF;

	    	--IF (PORDER.production_line) THEN
	    	--	RAISE NOTICE 'PRODUCTION LINE';
	    	--ELSE 
	    		SELECT * INTO PORDER FROM porders WHERE id = TR.porder_id;
	    		IF PORDER.status <> 'discarded' THEN
	    			PERFORM update_porder_status(TR.porder_id);
	    		END IF;	

	    	--END IF;
	    	RETURN TR;
	    END$$;


