-- Name: precence_records_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION precence_records_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$DECLARE
  TR RECORD;
  _FE BOOLEAN;
  _WAREHOUSE_ID INTEGER;
  _TOTAL_COST NUMERIC;
  _TOTAL_COST_RRHH NUMERIC;
  _TOTAL_COST_FUEL NUMERIC;
  _CURRENCY RECORD;
  _PROJECT_CURRENCY RECORD;
  _TOTAL NUMERIC;
BEGIN
  IF (TG_OP='DELETE') THEN
    TR := OLD;
  ELSE
    TR := NEW;
  END IF;

  -- FEATURE EXCLUSIVO DE INNOBRA
    SELECT feature('innobra') INTO _FE;

    IF _FE AND TR.project_id IS NOT NULL THEN
      
      SELECT warehouse_id INTO _WAREHOUSE_ID FROM projects_warehouses WHERE project_id = TR.project_id;
      SELECT COALESCE(SUM(total_cost),0) INTO _TOTAL_COST FROM invtransferences WHERE dst_warehouse_id = _WAREHOUSE_ID;
      SELECT COALESCE(SUM(cost),0) INTO _TOTAL_COST_RRHH FROM precence_records WHERE project_id = TR.project_id;
      SELECT COALESCE(SUM(cost),0) INTO _TOTAL_COST_FUEL FROM projects_fuels_orders WHERE project_id = TR.project_id;

      SELECT * INTO _PROJECT_CURRENCY FROM currencies WHERE id = (SELECT currency_id FROM projects WHERE id = TR.project_id);
      SELECT * INTO _CURRENCY FROM currencies WHERE "default" = true;
      _TOTAL := _TOTAL_COST+_TOTAL_COST_RRHH+_TOTAL_COST_FUEL;
      SELECT change_currency(_CURRENCY.id,_PROJECT_CURRENCY.id,_TOTAL) INTO _TOTAL;
      UPDATE projects SET total_expenses = _TOTAL WHERE id = TR.project_id;
      
    END IF;
  
  RETURN TR;
END
$$;


