			TR := OLD;
		ELSE
			TR := NEW;
		END IF;

		-- SE DEFINE ESTADO DE APROBACION
		IF TG_OP <> 'DELETE' THEN
			IF (NEW.invoice_number IS NOT NULL AND NEW.invoiced IS NULL) THEN
				TR.invoiced = NOW();
			END IF;

		  IF TG_OP = 'INSERT' AND TR.status IS NULL THEN
				TR.status = 'pending';
			END IF;
			-- SE ESTABLECE EL CODIGO DE FACTURACION DE LA AGENCIA)
			IF (TR.posale_id IS NOT NULL) THEN
				SELECT * INTO POSALE FROM posales WHERE id = TR.posale_id;
				TR.posale_billing_code := POSALE.bill_code;
				TR.agency_id := POSALE.agency_id;
			END IF;
		
			IF (TR.agency_id IS NOT NULL) THEN
				SELECT bill_code INTO TR.agency_billing_code FROM agencies WHERE id = TR.agency_id;
			END IF;

			IF (TR.contact_id IS NOT NULL) THEN
				SELECT notaxes INTO TR.notaxes FROM contacts WHERE contacts.id = TR.contact_id;
			END IF;
		
		END IF; 	

		IF (TG_OP = 'INSERT') THEN

			SELECT * INTO CURRENCY_PRICE FROM currencies_prices WHERE TR.issue_date BETWEEN currencies_prices.init_date AND currencies_prices.end_date AND currencies_prices.currency_id = TR.currency_id ORDER by id DESC LIMIT 1;
			-- SI HAY COTIZACION GENERADA
			IF ( TR.credit = true ) THEN
				TR.paid := 0;
			ELSE
				TR.paid := TR.amount;
			END IF;
			IF ( CURRENCY_PRICE.id IS NOT NULL ) THEN
				TR.currency_price := CURRENCY_PRICE.sale_price;
				TR.legal_currency_price := CURRENCY_PRICE.legal_sale_price;			
			-- SI NO EXISTE COTIZACION GENERADA, USAMOS LA COTIZACION ACTUAL
			ELSE

				SELECT * INTO CURRENCY FROM currencies WHERE currencies.id = TR.currency_id;
				IF ( CURRENCY.credit_prices AND TR.credit ) THEN
					TR.currency_price := CURRENCY.credit_sale_price;
				ELSE
					TR.currency_price := COALESCE(( SELECT sale_price FROM agencies_currencies WHERE agency_id = TR.agency_id AND currency_id = CURRENCY.id ), CURRENCY.sale_price);
				END IF;
				-- SELECT currencies.buy_price INTO TR.currency_price FROM currencies WHERE currencies.id = TR.currency_id;
				SELECT currencies.sale_price INTO TR.local_currency_price FROM currencies WHERE currencies."local" = true;

				SELECT currencies.legal_sale_price INTO TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
				SELECT currencies.legal_sale_price INTO TR.legal_local_currency_price FROM currencies WHERE currencies."local" = true;

			END IF;

			-- SELECCION DEL JOURNAL
			IF (TR.journal_id IS NULL AND TR.status = 'approved') THEN
				SELECT posales.current_journal_id INTO TR.journal_id FROM posales WHERE posales.id = TR.posale_id;
			END IF;

			IF (TR.campaign_id IS NOT NULL) THEN
				select finish_date into TR.expiration_date from campaigns where id=TR.campaign_id;
			END IF;
		
			TR.amount=(select round(TR.amount,currencies.decimals) from currencies where id=TR.currency_id);

			-- DEFINE EL TIMBRADO
			IF (TR.posale_id IS NOT NULL) THEN
				TR.stamping_id := POSALE.stamping_id;
				SELECT * INTO STAMPING FROM stampings WHERE id = TR.stamping_id;
				TR.stamping_code := STAMPING.code;
			END IF;
		ELSE
			IF (TG_OP = 'UPDATE') THEN
				-- SELECCION DEL JOURNAL
				IF (TR.journal_id IS NULL AND TR.status = 'approved') THEN
					SELECT posales.current_journal_id INTO TR.journal_id FROM posales WHERE posales.id = TR.posale_id;
				END IF;
				IF (NEW.currency_id != OLD.currency_id OR OLD.credit <> NEW.credit) THEN -- Lo del credito es para la cotizacion diferenciada
					RAISE NOTICE 'SE MODIFICA LA COTIZACION DE LA MONEDA';
					SELECT * INTO CURRENCY FROM currencies WHERE currencies.id = TR.currency_id;
					IF ( CURRENCY.credit_prices AND TR.credit ) THEN
						TR.currency_price := CURRENCY.credit_buy_price;
					ELSE
						TR.currency_price := CURRENCY.buy_price;
					END IF;
					-- SELECT currencies.buy_price INTO TR.currency_price FROM currencies WHERE currencies.id = TR.currency_id;
					SELECT currencies.buy_price INTO TR.local_currency_price FROM currencies WHERE currencies."local" = true;

					SELECT currencies.legal_buy_price INTO TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
					SELECT currencies.legal_buy_price INTO TR.legal_local_currency_price FROM currencies WHERE currencies."local" = true;
				END IF;
				TR.amount=(select round(TR.amount,currencies.decimals) from currencies where id=TR.currency_id);

				-- DEFINE EL TIMBRADO
				IF (NEW.stamping_id IS NULL OR ( NEW.posale_id IS NOT NULL AND NEW.posale_id <> OLD.posale_id )) THEN
					TR.stamping_id := POSALE.stamping_id;
					SELECT * INTO STAMPING FROM stampings WHERE id = TR.stamping_id;
					TR.stamping_code := STAMPING.code;
				END IF;
			END IF;
		END IF;

		-- SE DEFINE EL IMPUESTO APLICADO A LOS INTERESES SI HAY
		IF (TR.total_interest > 0) THEN
			TR.interest_tax_id = ( SELECT id FROM taxes WHERE interests = TRUE ORDER BY rate LIMIT 1  );
			IF (TR.interest_tax_id IS NOT NULL) THEN
				TR.interest_tax_rate = COALESCE(( SELECT rate FROM taxes WHERE id = TR.interest_tax_id ), 0);
				TR.interest_tax_amount = TR.total_interest * TR.interest_tax_rate / 100;
			END IF;
		END IF;

	
		RETURN TR;
	END
	$$;


