-- Name: product_total_expenses(bigint); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION product_total_expenses(arg_product bigint) RETURNS integer
    LANGUAGE plpgsql
    AS $$
BEGIN	
	--UPDATE
	--	products
	--SET
	--	total_expenses = COALESCE( (SELECT SUM(quantity*price) FROM products_stocks WHERE product_id = products.id), 0) + COALESCE( (SELECT SUM(price) FROM product_expenses WHERE product_id = products.id), 0)
	--WHERE
	--	id = arg_product
	--;

	UPDATE products SET total_direct_expenses = COALESCE((

		SELECT
			SUM(amount)
		FROM (

			SELECT
				SUM(product_expenses.price * currency_price) AS "amount",
				1 AS "type"
			FROM
				product_expenses
			WHERE
				product_expenses.product_id = arg_product
				
			UNION
			
			SELECT
				SUM(products_stocks.price * products_stocks.quantity * currency_price) AS "amount",
				2 AS "type"
			FROM
				products_stocks
			WHERE
				products_stocks.product_id = arg_product
				
			UNION
			
			SELECT
				SUM(stock_assignations_products.quantity * stock_assignations_products.price * stock_assignations.currency_price) AS "amount",
				3 AS "type"
			FROM
				stock_assignations,
				stock_assignations_products
			WHERE
				stock_assignations.product_id = arg_product
			AND
				stock_assignations_products.stock_assignation_id = stock_assignations.id
			
		) AS FOO
		
	), 20);
	
	RETURN 1;
END
$$;


