CREATE FUNCTION products_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
				declare
					TR RECORD;
					TAX taxes%ROWTYPE;
					CURRENCY currencies%ROWTYPE;
				begin 
					IF (TG_OP='DELETE') THEN
						TR=OLD;
					ELSE
