						END IF;

						IF ( TR.buy_price_pp = 0 ) THEN
							TR.buy_price_pp := TR.buy_price;
							IF ( TR.tax_id IS NOT NULL ) THEN
								TR.buy_price_pp_untaxed := ROUND( ( TR.buy_price_pp / ( 1 + ( TAX.rate / 100 ) ) ), CURRENCY.decimals);
							ELSE
								TR.buy_price_pp_untaxed := TR.buy_price_pp;
							END IF;
						END IF;
					END IF;

					RETURN TR;
				end;
				$$;


