--

CREATE FUNCTION products_before_unique_code() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	AUX INTEGER;
BEGIN
	IF (NEW.code IS NOT NULL AND NEW.code <> '') THEN
		IF (NEW.code2 IS NOT NULL AND NEW.code2 <> '') THEN
			-- SE INGRESO CODE Y CODE2
			SELECT
				COUNT(*) INTO AUX
			FROM
				products
			WHERE
				products.id <> NEW.id
			AND (
				products.code = NEW.code OR products.code2 = NEW.code
				OR
				products.code = NEW.code2 OR products.code2 = NEW.code2
			)
			;
		ELSE
			-- SE INGRESO SOLO CODE
			SELECT
				COUNT(*) INTO AUX
			FROM
				products
			WHERE
				products.id <> NEW.id
			AND (
				products.code = NEW.code OR products.code2 = NEW.code
			)
			;
		END IF;
	ELSE
		IF (NEW.code2 IS NOT NULL AND NEW.code2 <> '') THEN
			-- SE INGRESO SOLO CODE2
			SELECT
				COUNT(*) INTO AUX
			FROM
				products
			WHERE
				products.id <> NEW.id
			AND (
				products.code = NEW.code2 OR products.code2 = NEW.code2
			)
			;
		END IF;
	END IF;

	IF (AUX > 0) THEN
		RETURN NULL;
	ELSE
		RETURN NEW;
	END IF;
END
$$;


