CREATE FUNCTION projects_fuels_orders_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$DECLARE
  TR RECORD;
  _WAREHOUSE_ID INTEGER;
  _TOTAL_COST NUMERIC;
  _TOTAL_COST_RRHH NUMERIC;
  _TOTAL_COST_FUEL NUMERIC;
  _CURRENCY RECORD;
  _PROJECT_CURRENCY RECORD;
  _TOTAL NUMERIC;
