	TR RECORD;
	CURRENCY_PRICE currencies_prices%ROWTYPE;
BEGIN

	IF TG_OP='DELETE' THEN
		TR:=OLD;
	ELSE
		TR:=NEW;
	END IF;

	SELECT * INTO CURRENCY_PRICE FROM currencies_prices WHERE TR.issue_date BETWEEN currencies_prices.init_date AND currencies_prices.end_date AND currencies_prices.currency_id = TR.currency_id ORDER by id DESC LIMIT 1;
	
	IF (TG_OP = 'INSERT') THEN
		IF ( CURRENCY_PRICE.id IS NOT NULL ) THEN
			TR.currency_price := CURRENCY_PRICE.buy_price;
			TR.legal_currency_price := CURRENCY_PRICE.legal_buy_price;
		ELSE
			SELECT currencies.buy_price, currencies.legal_buy_price INTO TR.currency_price, TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
		END IF;
	ELSE
		IF (TG_OP = 'UPDATE') THEN
			IF (OLD.currency_id <> NEW.currency_id) THEN
				IF ( CURRENCY_PRICE.id IS NOT NULL ) THEN
					TR.currency_price := CURRENCY_PRICE.buy_price;
					TR.legal_currency_price := CURRENCY_PRICE.legal_buy_price;					
				ELSE
					SELECT currencies.buy_price, currencies.legal_buy_price INTO TR.currency_price, TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
				END IF;				
			END IF;
		END IF;
	END IF;
	
	RETURN TR;
