--
-- Name: purchases_before(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION purchases_before() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
DECLARE
  TR RECORD;
  VAR_AMOUNT NUMERIC;
  FUND_ACCOUNT fund_accounts%ROWTYPE;
  CURRENCY RECORD;
  CURRENCY_PRICE currencies_prices%ROWTYPE;
  _project_id INTEGER;
  _FE BOOLEAN;
BEGIN
  IF (TG_OP='DELETE') THEN
    TR := OLD;
  ELSE
    TR := NEW;
  END IF;

  -- SE GUARDA LA COTIZACION ACTUAL DE LA MONEDA
  IF (TG_OP = 'INSERT' ) THEN
      
    IF (TR.currency_price IS NULL OR TR.currency_price = 0) THEN

      SELECT * INTO CURRENCY_PRICE FROM currencies_prices WHERE TR.issue_date BETWEEN currencies_prices.init_date AND currencies_prices.end_date AND currencies_prices.currency_id = TR.currency_id ORDER by id DESC LIMIT 1;
      SELECT * INTO CURRENCY FROM currencies WHERE currencies.id = TR.currency_id;
      
      IF ( CURRENCY_PRICE.id IS NOT NULL ) THEN
       TR.legal_currency_price := CURRENCY_PRICE.legal_sale_price;
       TR.currency_price := CURRENCY_PRICE.sale_price;
      ELSE
       SELECT currencies.sale_price INTO TR.currency_price FROM currencies WHERE currencies.id = NEW.currency_id;
       SELECT currencies.sale_price INTO TR.local_currency_price FROM currencies WHERE currencies."local" = true;

       SELECT currencies.legal_sale_price INTO TR.legal_currency_price FROM currencies WHERE currencies.id = NEW.currency_id;
       SELECT currencies.legal_sale_price INTO TR.legal_local_currency_price FROM currencies WHERE currencies."local" = true;

      END IF;
      TR.currency_manual = FALSE;
    ELSE
      TR.currency_manual = TRUE;
    END IF;   
  ELSE
    IF (TG_OP = 'UPDATE') THEN
      IF (new.currency_id != old.currency_id OR new.update_currency_price = TRUE) AND TR.currency_manual = FALSE THEN
         SELECT currencies.sale_price INTO TR.currency_price FROM currencies WHERE currencies.id = NEW.currency_id;
         SELECT currencies.sale_price INTO TR.local_currency_price FROM currencies WHERE currencies."local" = true;

         SELECT currencies.legal_sale_price INTO TR.legal_currency_price FROM currencies WHERE currencies.id = NEW.currency_id;
         SELECT currencies.legal_sale_price INTO TR.legal_local_currency_price FROM currencies WHERE currencies."local" = true;

         TR.update_currency_price = FALSE;
      END IF;
      
    END IF;
  END IF;

  IF(TG_OP='UPDATE' OR TG_OP='INSERT') THEN
    SELECT * INTO CURRENCY FROM currencies where id=TR.currency_id;
    TR.amount=(select round(TR.amount,CURRENCY.decimals));
    TR.cn_discount=(select round(TR.cn_discount,CURRENCY.decimals));
  END IF;

  -- Feature exclusivo para Innobra
  SELECT feature('innobra') INTO _FE;

  IF _FE THEN
    IF (TG_OP <> 'DELETE') THEN
      IF TR.warehouse_id IS NOT NULL THEN
        SELECT project_id INTO _project_id FROM projects_warehouses WHERE warehouse_id = TR.warehouse_id;
        IF _project_id IS NOT NULL THEN
          TR.project_id = _project_id;
        ELSE
          TR.project_id = null;
        END IF;
      END IF;
    END IF;
  END IF;
    
  RETURN TR;
END
$$;


