-- Name: purchases_expenses_before_insert_update(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION purchases_expenses_before_insert_update() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
	TAX taxes%ROWTYPE;
	EXPENSE expenses%ROWTYPE;
	PURCHASE purchases%ROWTYPE;
	UNTAXEDVAL NUMERIC;
	CURRENCY RECORD;
BEGIN
	SELECT expenses.* INTO EXPENSE FROM expenses WHERE id = NEW.expense_id;
	SELECT purchases.* INTO PURCHASE FROM purchases WHERE id = NEW.purchase_id;

	SELECT * INTO CURRENCY FROM currencies WHERE id=(select currency_id from purchases where id=NEW.purchase_id);

	-- SELECT taxes.* INTO TAX FROM taxes, purchases WHERE taxes.id IN ( SELECT tax_id FROM products_taxes WHERE product_id = NEW.product_id LIMIT 1 ) AND purchases.id = NEW.purchase_id AND purchases.billed = TRUE AND purchases.tax = TRUE;

	IF (NEW.discount_amount > 0) THEN
		NEW.discount_amount := NEW.discount_amount;
		NEW.discount_rate := NEW.discount_amount * 100 / NEW.price;
		NEW.price := NEW.price - (NEW.discount_amount);
	ELSIF (NEW.discount_rate > 0) THEN
		NEW.discount_amount := NEW.discount_rate * NEW.price / 100;
		NEW.price := NEW.price - NEW.discount_amount;
	END IF;

	IF ( PURCHASE.billed = TRUE AND PURCHASE.doctype = 'invoice' ) THEN
		-- DETERMINACION DE IMPUESTOS
		IF ( NEW.manual_taxed = TRUE ) THEN
			SELECT taxes.* INTO TAX FROM taxes, purchases WHERE NEW.tax_id = taxes.id AND purchases.id = NEW.purchase_id AND purchases.billed = TRUE AND purchases.tax = TRUE LIMIT 1;
		ELSE
			SELECT taxes.* INTO TAX FROM taxes, expenses, purchases WHERE expenses.id = NEW.expense_id AND expenses.tax_id = taxes.id AND purchases.id = NEW.purchase_id AND purchases.billed = TRUE AND purchases.tax = TRUE LIMIT 1;
		END IF;
		NEW.tax_id := TAX.id;
		NEW.tax_rate := COALESCE(TAX.rate, 0);
		IF (TAX.rate IS NOT NULL) THEN
			-- NEW.tax_amount := NEW.price * TAX.rate * ( EXPENSE.taxable / 100 ) / (100 + TAX.rate);
			-- Primero se desagrega el impuesto
			UNTAXEDVAL := ROUND(NEW.price / ( 1 + ( EXPENSE.taxable / 100 ) * ( TAX.rate / 100 ) ), CURRENCY.decimals);
			NEW.tax_amount := ROUND(NEW.price - UNTAXEDVAL, CURRENCY.decimals);
		END IF;
		-- FIN DETERMINACION DE IMPUESTOS
	END IF;
	IF(TG_OP='UPDATE' OR TG_OP='INSERT') THEN
		NEW.price=round(NEW.price,CURRENCY.decimals);
	END IF;

	RETURN NEW;
END
$$;


