--
-- Name: purchases_fund_accounts_fn(integer); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION purchases_fund_accounts_fn(purchaseid integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
declare
	purchaseFundAccountsCursor cursor for select * from purchases_fund_accounts 
	where purchase_id=purchaseId and fund_account_id is not NULL;
	PURCHASE RECORD;
	PURCHASEFUNDACCOUNT purchases_fund_accounts%ROWTYPE;
	FUNDACCOUNT fund_accounts%ROWTYPE;
	contador integer:=0;
	APPLDATE TIMESTAMP WITHOUT TIME ZONE;
	VNOTES TEXT;
	V_AMOUNT NUMERIC;
begin

	SELECT INTO PURCHASE * FROM purchases where id=purchaseId;
	
	IF (PURCHASE.status <> 'active') THEN
		RETURN 0;
	END IF;

	IF (PURCHASE.journal_id IS NOT NULL) THEN
		SELECT created INTO APPLDATE FROM journals WHERE id = PURCHASE.journal_id;
	END IF;

	VNOTES := 'PURCHASE #'||PURCHASE.id;

	IF (PURCHASE.check_no IS NOT NULL AND PURCHASE.check_no <> '') THEN
		VNOTES := PURCHASE.check_no || ' ';
	END IF;

	IF (PURCHASE.notes IS NOT NULL AND PURCHASE.notes <> '') THEN
		VNOTES := VNOTES || PURCHASE.notes || ' ';
	END IF;
	
	DELETE FROM fat_purchases WHERE purchase_id = purchaseid;

	open purchaseFundAccountsCursor;
	fetch purchaseFundAccountsCursor into PURCHASEFUNDACCOUNT;
	while found loop
		V_AMOUNT := PURCHASEFUNDACCOUNT.amount;
		-- SE OBTIENE LA MONEDA DE LA CUENTA
		SELECT * INTO FUNDACCOUNT FROM fund_accounts WHERE id = PURCHASEFUNDACCOUNT.fund_account_id;
		IF (FUNDACCOUNT.currency_id <> PURCHASE.currency_id) THEN
			V_AMOUNT := V_AMOUNT * PURCHASE.currency_price;
			V_AMOUNT := V_AMOUNT / ( SELECT sale_price FROM currencies WHERE id = FUNDACCOUNT.currency_id );
		END IF;

		IF ( FUNDACCOUNT.imprest ) THEN
			APPLDATE := PURCHASE.created;
		ELSE
			IF (PURCHASE.issue_date = DATE(PURCHASE.created)) THEN
				APPLDATE := PURCHASE.created;
			ELSE
				-- SE VERIFICA SI LA CUENTA DE FONDO ESTABA AFECTADA POR UNA JORNADA EN ESA FECHA
				-- SELECT * FROM journals;
				APPLDATE := COALESCE((SELECT open_tm + INTERVAL '1 minute' FROM journals WHERE fund_account_id = PURCHASEFUNDACCOUNT.fund_account_id AND DATE(journals.open_tm) = PURCHASE.issue_date LIMIT 1), (PURCHASE.issue_date::varchar || ' 21:00:00')::timestamp);
			END IF;
		END IF;

		INSERT INTO fund_account_transactions (
			fund_account_id,
			amount,
			currency_id,
			issue_date,
			appl_date,
			method,
			user_id,
			notes,
			contact_id,
			code
		)
		VALUES(
			PURCHASEFUNDACCOUNT.fund_account_id,
			V_AMOUNT * -1,
			PURCHASE.currency_id,
			APPLDATE,
			APPLDATE,
			'cash_purchase',
			PURCHASE.user_id,
			VNOTES,
			PURCHASE.contact_id,
			PURCHASEFUNDACCOUNT.check_number
		);

		INSERT INTO fat_purchases (
			purchase_id,
			fund_account_transaction_id
		)
		VALUES (
			PURCHASE.id,
			( SELECT CURRVAL('fund_account_transactions_id_seq') )
		);

		contador:=contador+1;
		fetch purchaseFundAccountsCursor into PURCHASEFUNDACCOUNT;
	end loop;

	close purchaseFundAccountsCursor;

return contador;
end;
$$;


