-- Name: purchases_products_before_insert_update(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION purchases_products_before_insert_update() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
DECLARE
	DECLARE TAX taxes%ROWTYPE;
	DECLARE PURCHASE purchases%rowtype;
	DECLARE PRODUCT products%rowtype;
	CURRENCY RECORD;
	ROUNDIT BOOLEAN;
BEGIN
	SELECT * INTO PURCHASE FROM purchases WHERE id = NEW.purchase_id;
	SELECT * INTO CURRENCY FROM currencies WHERE id = PURCHASE.currency_id;

	SELECT products.* INTO PRODUCT FROM products WHERE products.id = NEW.product_id;

	ROUNDIT := TRUE;
	
	-- DETERMINACION DE LA CANTIDAD SEGUN LA UNIDAD DE MEDIDA ESTANDARD DEL PRODUCTO
	--IF (NEW.aum_id IS NULL) THEN
--		NEW.aum_factor = 1;
--	ELSE
--		SELECT quantity INTO NEW.aum_factor FROM aums WHERE id = NEW.aum_id;
--		ROUNDIT := FALSE;
--	END IF;

	IF (NEW.product_um_id IS NULL) THEN
		NEW.aum_factor = 1;
	ELSE
		SELECT factor INTO NEW.aum_factor FROM product_ums WHERE id = NEW.product_um_id;
		ROUNDIT := FALSE;
	END IF;

	NEW.quantity = NEW.quantity * NEW.aum_factor;
	NEW.price = NEW.price / NEW.aum_factor;

	NEW.aum_id := NULL;
	NEW.aum_factor := 1;
	NEW.product_um_id := NULL;

	IF (TG_OP = 'INSERT') THEN

		NEW.prior_buy_price_pp := PRODUCT.buy_price_pp;
		NEW.prior_quantity := NEW.quantity;
		NEW.prior_availability := PRODUCT.availability;
		NEW.prior_buy_price_pp_untaxed := PRODUCT.buy_price_pp_untaxed;
		
		IF (NEW.discount_amount > 0) THEN
			-- NEW.discount_amount := NEW.discount_amount;
			NEW.discount_rate := NEW.discount_amount / NEW.quantity * 100 / NEW.price;
			NEW.price := NEW.price - (NEW.discount_amount / NEW.quantity);
		ELSIF (NEW.discount_rate > 0) THEN
			NEW.discount_amount := NEW.discount_rate * NEW.price / 100;
			NEW.price := NEW.price - NEW.discount_amount;
		END IF;
	END IF;
	
	-- DETERMINACION DE IMPUESTOS
	IF (PURCHASE.manual_taxed = FALSE) THEN
		IF (NEW.tourism_scheme = FALSE AND PURCHASE.import = FALSE AND PURCHASE.tax = TRUE AND PURCHASE.billed = TRUE AND PURCHASE.doctype = 'invoice') THEN
			RAISE NOTICE 'IMPUESTOS';

			SELECT taxes.* INTO TAX FROM taxes WHERE taxes.id IN (SELECT products.sale_tax_id FROM products WHERE products.id = NEW.product_id LIMIT 1);
			--SELECT taxes.* INTO TAX FROM taxes, purchases WHERE taxes.id IN ( SELECT products.sale_tax_id FROM products WHERE products.id = NEW.product_id LIMIT 1 ) AND purchases.id = NEW.purchase_id AND purchases.billed = TRUE AND purchases.tax = TRUE;
			RAISE NOTICE 'NEW.product_id=%', NEW.product_id;
			RAISE NOTICE 'TAX.id=%', TAX.id;
			
			
			NEW.tax_id := TAX.id;
			NEW.tax_rate := COALESCE(TAX.rate, 0);
			-- $monto[pg_result($rimp, $imp, 'imp_porc')] - ( $monto[pg_result($rimp, $imp, 'imp_porc')] * pg_result($rimp, $imp, 'imp_porc') / (100 + pg_result($rimp, $imp, 'imp_porc')))
			-- NEW.tax_amount := NEW.price * COALESCE(TAX.rate, 0) / 100;
			IF (TAX.rate IS NOT NULL) THEN
				-- NEW.tax_amount := (NEW.price * NEW.quantity  - NEW.discount_amount ) * (PRODUCT.taxable / 100) * TAX.rate / (100 + TAX.rate);
				NEW.tax_amount := (NEW.price * NEW.quantity  ) * (PRODUCT.purchase_taxable / 100) * TAX.rate / (100 + TAX.rate);
				NEW.tax_amount := ROUND( NEW.tax_amount, CURRENCY.decimals );
			END IF;
		ELSE
			--RAISE NOTICE 'SIN IMPUESTOS';
			NEW.tax_rate := 0;
			NEW.tax_amount := 0;
			NEW.tax_id := NULL;
		END IF;
	END IF;

	IF (NEW.tax_id IS NOT NULL AND NEW.tax_amount > 0 AND NEW.tax_rate = 0) THEN
		SELECT "rate" INTO NEW.tax_rate FROM taxes WHERE taxes.id = NEW.tax_id;
	END IF;

		IF(PURCHASE.doctype = 'auto_invoice') THEN
			NEW.tax_id := null;
			NEW.tax_rate := 0;
			NEW.tax_amount := 0;
		END IF;
	
	-- FIN DETERMINACION DE IMPUESTOS

	RAISE NOTICE 'ROUNDIT=%', ROUNDIT;

	IF(TG_OP='UPDATE' OR TG_OP='INSERT') AND NEW.aum_id IS NOT NULL THEN
		-- SELECT * INTO CURRENCY FROM currencies WHERE id=(select currency_id from purchases where id=NEW.purchase_id);
		NEW.price=round(NEW.price,CURRENCY.decimals);
	END IF;
	
	NEW.aum_id = NULL;
	NEW.aum_factor = 1;

	RETURN NEW;
END
$_$;


