DECLARE
  TR RECORD;
  PURCHASE purchases%ROWTYPE;
  -- PTYPE ptypes%ROWTYPE;
  STOCK_STATUS CHARACTER VARYING;
  STOCK_RECEIVED TIMESTAMP WITHOUT TIME ZONE;
  STOCK_TOTAL_RECEIVED NUMERIC;
  TOTAL_TOURISM NUMERIC;
  PRODUCT products%ROWTYPE;
BEGIN
  IF (TG_OP='DELETE') THEN
    TR := OLD;
    DELETE FROM stocks WHERE purchases_product_id = TR.id;
  ELSE
    TR := NEW;

    SELECT * INTO PRODUCT FROM products WHERE id = TR.product_id;
    IF (PRODUCT.stockable = FALSE) THEN
      RETURN NEW;
    END IF;
    
    SELECT * INTO PURCHASE FROM purchases WHERE id = TR.purchase_id;

	DELETE FROM stocks WHERE purchases_product_id = TR.id;

	IF ( PURCHASE.incon_id IS NOT NULL ) THEN
		RETURN TR;
	END IF;
    
    -- SELECT ptypes.* INTO PTYPE FROM ptypes, products WHERE products.id = TR.product_id AND products.ptype_id = ptypes.id;
    IF ( PURCHASE.delivery_submit AND PURCHASE.status = 'active' AND PURCHASE.warehouse_id IS NOT NULL ) THEN
      STOCK_STATUS := 'received';
      IF (PURCHASE.issue_date = DATE(NOW())) THEN
        STOCK_RECEIVED := NOW();
      ELSE
        STOCK_RECEIVED := PURCHASE.issue_date;
      END IF;
      STOCK_TOTAL_RECEIVED := TR.quantity - TR.returned;
    ELSE
      STOCK_STATUS := 'pending';
      STOCK_RECEIVED := NULL;
      STOCK_TOTAL_RECEIVED := 0;
    END IF;
    -- IF ( PURCHASE.tourism_scheme AND PTYPE.tourism_scheme ) THEN
    IF ( TR.tourism_scheme ) THEN
      TOTAL_TOURISM := TR.quantity - TR.returned;
    ELSE
      TOTAL_TOURISM := 0;
    END IF;
   
    INSERT INTO stocks (
      product_id,
      total_sent,
      price,
      currency_id,
      warehouse_id,
      total,
      total2,
      received,
      purchase_id,
      status,
