					SELECT currencies.buy_price, currencies.legal_buy_price INTO TR.currency_price, TR.legal_currency_price FROM currencies WHERE currencies.id = TR.currency_id;
				END IF;				
			END IF;
		END IF;
	END IF;
	

	IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN
		SELECT bill_code INTO TR.agency_billing_code FROM agencies WHERE agencies.id = NEW.agency_id;
		SELECT bill_code INTO TR.posale_billing_code FROM posales WHERE posales.id = NEW.posale_id;

