		IF (TAX.rate IS NOT NULL) THEN
			NEW.tax_amount := ( NEW.price - NEW.discount ) * TAX.rate * ( EXPENSE.taxable / 100 ) / (100 + TAX.rate);
		END IF;
		-- FIN DETERMINACION DE IMPUESTOS

		RETURN NEW;
	END
