      IF (TAX.rate IS NOT NULL) THEN
        NEW.tax_amount := ((NEW.price * NEW.quantity - NEW.discount) * (PRODUCT.taxable / 100)) * TAX.rate / (100 + TAX.rate);
        NEW.tax_amount := ROUND(NEW.tax_amount, CURRENCY.decimals);
      ELSE
