      IF (CONTACT.notaxes = TRUE) THEN
        -- NEW.price := NEW.price - NEW.tax_amount;
        NEW.tax_id := NULL;
        NEW.tax_rate := 0;
        NEW.tax_amount := 0;
      END IF;
    END IF;

    -- FIN DETERMINACION DE IMPUESTOS

    RETURN NEW;
  END
