--

CREATE FUNCTION sales_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
DECLARE	
	TR RECORD;
	PRICE NUMERIC;
	COMMISSION NUMERIC;
  _CURRENCY RECORD;
  _PROJECT_CURRENCY RECORD;
	_SALES REFCURSOR;
	_ROW RECORD;
	_TOTAL NUMERIC;
	_TOTAL_PAID NUMERIC;
BEGIN
	IF (TG_OP='DELETE') THEN
		TR := OLD;
	ELSE
		TR := NEW;
	END IF;

	IF (TR.invoice_number = '') THEN
		TR.invoice_number := null;
	END IF;

	IF (TG_OP = 'INSERT' AND TR.invoice_number IS NOT NULL AND TR.posale_id IS NOT NULL) THEN
		IF (TR.billed) THEN
			UPDATE posales SET latest_invoice_number = TR.invoice_number::BIGINT WHERE id = TR.posale_id;
		ELSE
			UPDATE posales SET latest_alt_invoice_number = TR.invoice_number::BIGINT WHERE id = TR.posale_id;
		END IF;
	ELSE
		IF (TG_OP = 'UPDATE') THEN
			IF (NEW.invoice_number = '') THEN
				NEW.invoice_number := null;
			END IF;
			IF ( OLD.invoice_number IS NULL AND NEW.invoice_number IS NOT NULL ) THEN
				IF (TR.billed) THEN
					UPDATE posales SET latest_invoice_number = TR.invoice_number::BIGINT WHERE id = TR.posale_id;
				ELSE
					UPDATE posales SET latest_alt_invoice_number = TR.invoice_number::BIGINT WHERE id = TR.posale_id;
				END IF;
			END IF;
			IF ( OLD.stock <> NEW.stock ) THEN
				UPDATE
					sales_products
				SET	
					stock = NEW.stock
				WHERE
					sales_products.sale_id = NEW.id
				;
			END IF;
		END IF;
	END IF;


	IF (TR.delivery_zone_id IS NOT NULL) THEN
		SELECT delivery_zones.commission INTO COMMISSION FROM delivery_zones WHERE id = TR.delivery_zone_id;
		DELETE FROM sales_drivers_commissions WHERE sale_id = TR.id;
		IF ( COMMISSION > 0) THEN
			INSERT INTO sales_drivers_commissions 
			(
				contact_id,
				sale_id,
				amount
			)
			VALUES
			(
				TR.driver_contact_id,
				TR.id,
				COMMISSION
			);
		END IF;
	END IF;
	
	IF (TR.project_id IS NOT NULL) THEN
		_TOTAL := 0;
   		SELECT * INTO _PROJECT_CURRENCY FROM currencies WHERE id = (SELECT currency_id FROM projects WHERE id = TR.project_id);
		OPEN _SALES FOR SELECT * FROM sales WHERE project_id = TR.project_id AND void = false;
		LOOP
			FETCH _SALES INTO _ROW;
			EXIT WHEN NOT FOUND;
			IF(_ROW.currency_id <> _PROJECT_CURRENCY.id) THEN
				_TOTAL := _TOTAL + ( _ROW.amount * _ROW.currency_price / _PROJECT_CURRENCY.buy_price );
				_TOTAL_PAID := _TOTAL_PAID + ( _ROW.paid * _ROW.currency_price / _PROJECT_CURRENCY.buy_price );
			ELSE
				_TOTAL := _TOTAL + ( _ROW.amount );
				_TOTAL_PAID := _TOTAL_PAID + ( _ROW.paid );
			END IF;
		END LOOP;

		UPDATE
			projects
		SET
			invoiced = COALESCE(_TOTAL, 0),
			paid = COALESCE(_TOTAL_PAID, 0)
		WHERE
			projects.id = TR.project_id
		;
	END IF;
	
	IF (TR.down_payment_sale_id IS NOT NULL) THEN
		UPDATE
			sales
		SET
			down_payment_used = COALESCE(
				(
					SELECT
						SUM(s2.discount)
					FROM
						sales s2
					WHERE
						s2.down_payment_sale_id = sales.id
					AND
						s2.void = FALSE
				),
				0
			)
		WHERE
			sales.id = TR.down_payment_sale_id
		;
	END IF;

	IF (TG_OP='DELETE') THEN
		DELETE FROM accentries WHERE operation_type IN ( 'sale', 'sale_cost', 'xfer_sale', 'sale_void', 'xfer_sale_void', 'unbilled_sale_reversion' ) AND operation_id = OLD.id AND "active" = FALSE;
	END IF;

	RETURN TR;
END
$$;


