  			IF (NEW.status <> 'pending' AND OLD.status = 'pending') THEN
  				UPDATE "tables" SET status = 'free' WHERE id = TR.table_id;
  			END IF;
  			IF (NEW.status = 'pending' AND NEW.preinvoice = TRUE) THEN
  				UPDATE tables SET status = 'preinvoice' WHERE id = TR.table_id;
  			END IF;
  		END IF;
  	END IF;
		
  	RETURN TR;
