CREATE FUNCTION sales_delivery_notes_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
  DECLARE
  	TR RECORD;
  	COUNT_SALES integer;
  BEGIN
  	IF (TG_OP='DELETE') THEN
  		TR := OLD;
  	ELSE
  		TR := NEW;
  	END IF;

  	IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN
  		UPDATE
  			leasings
  		SET
  			delivered = TRUE,
  			delivered_timestamp = NOW()
  		WHERE
  			delivered = FALSE
  		AND
  			sales_product_id IN (
  				SELECT
  					sales_products.id
  				FROM
  					sales_products
  				WHERE
  					sales_products.sale_id = NEW.sale_id
  			)
  		;
  	END IF;

  	SELECT COUNT(*) INTO COUNT_SALES FROM sales_delivery_notes WHERE delivery_note_id = TR.delivery_note_id;
	
  	IF (COUNT_SALES > 0) THEN
  		UPDATE delivery_notes SET status = 'invoiced' WHERE id = TR.delivery_note_id;
  	ELSE
  		UPDATE delivery_notes SET status = 'pending' WHERE id = TR.delivery_note_id;
  	END IF;
	
  	RETURN TR;
  END
  $$;


