-- Name: sales_payments_after(); Type: FUNCTION; Schema: public; Owner: dev
--

CREATE FUNCTION sales_payments_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$ 
        DECLARE
        	PM payment_methods%ROWTYPE;
        	SALE sales%ROWTYPE;
        	POSALE posales%ROWTYPE;
        	COLLECTION collections%ROWTYPE;
        	FAC BIGINT;
        	TR RECORD;
        	APPLDATE TIMESTAMP WITHOUT TIME ZONE;
        	SALE_AMOUNT NUMERIC;
        	ADVANCE_AMOUNT NUMERIC;
        	RELATION_AMOUNT NUMERIC;
      	PPID BIGINT;
      	VAR_COMISION NUMERIC;
      	VAR_RET_IVA NUMERIC;
      	VAR_RET_RENTA NUMERIC;
      	VAR_AMOUNT NUMERIC;
      	CFEE payment_method_fees%ROWTYPE;
      	V_NOTES VARCHAR;
    	_CURRENCY RECORD;
    	VAR_FUND_ACCOUNT_ID BIGINT;
        BEGIN
        	IF (TG_OP='DELETE') THEN
        		TR := OLD;
        	ELSE
        		TR := NEW;
        	END IF;

        	V_NOTES := '';

        	-- SE DETERMINA SI LA VENTA FUE FACTURADA A CREDITO O A CONTADO
        	PERFORM set_sale_invoice_type(TR.sale_id);

        	-- SE DETERMINA EL MONTO YA PAGADO DE LA VENTA
        	PERFORM set_sale_paid(TR.sale_id);

        	VAR_AMOUNT := TR.amount;

        	----------------------------------------------------------------
        	-- SE ELIMINAN LOS MOVIMIENTOS ANTERIORES
        	DELETE FROM fat_spm WHERE sales_payment_method_id = TR.id;

        	SELECT * INTO SALE FROM sales WHERE id = TR.sale_id;

    	SELECT * INTO _CURRENCY FROM currencies WHERE id = COALESCE(TR.currency_id, SALE.currency_id);

        	V_NOTES := 'INVOICE #' || SALE.invoice_number;

        	IF (SALE.void) THEN
    			RETURN TR;
        	END IF;


        	SELECT * INTO PM FROM payment_methods WHERE id = TR.payment_method_id;

        	V_NOTES := V_NOTES || PM.name || ' ' || COALESCE( TR.vaucher, TR.check_number, '' );

        	-- SELECT * FROM payment_methods

        	IF ((TG_OP='INSERT' OR TG_OP='UPDATE') AND TR.credit = false AND TR.delayed = false AND TR.advance = FALSE AND PM.expense = FALSE) THEN
        		-- Los movimientos en cuenta se borran automaticamente por
        		-- un trigger after delete sobre la tabla fat_spm 
	
        		--SELECT * INTO SALE FROM sales WHERE id = NEW.sale_id;
        		SELECT * INTO POSALE FROM posales WHERE id = SALE.posale_id;

        		IF (DATE(COALESCE(SALE.invoiced, SALE.created)) = SALE.issue_date OR SALE.journal_id IS NOT NULL) THEN
        			APPLDATE := COALESCE(SALE.invoiced, SALE.created);
        		ELSE
        			APPLDATE := SALE.issue_date;
        		END IF;

        		IF ( PM.days_to_deposit > 0 ) THEN
    				RAISE NOTICE 'APPLDATE BEFORE:%', APPLDATE;
      				APPLDATE := APPLDATE + ( PM.days_to_deposit::varchar || ' day' )::interval;
      				RAISE NOTICE 'APPLDATE AFTER:%', APPLDATE;
      			END IF;
	
      		VAR_COMISION := 0;
      		VAR_RET_IVA := 0;
      		VAR_RET_RENTA := 0;

      		VAR_COMISION := TR.commission;
      		VAR_RET_IVA := TR.withholding_vat;
      		VAR_RET_RENTA := TR.withholding_income;

	
        		-- SE CREA EL MOVIMIENTO EN CUENTA
    		RAISE NOTICE '%', NEW.amount;
    		RAISE NOTICE '%', VAR_COMISION;
    		RAISE NOTICE '%', VAR_RET_IVA;
    		RAISE NOTICE '%', VAR_RET_RENTA;

    		IF ( PM.fund_account_id IS NOT NULL ) THEN
    			VAR_FUND_ACCOUNT_ID := PM.fund_account_id;
    		ELSE
    			IF ( NEW.currency_price = 1 ) THEN
    				VAR_FUND_ACCOUNT_ID := POSALE.fund_account_id;
    			ELSE
    				SELECT fund_account_id INTO VAR_FUND_ACCOUNT_ID FROM posales_currency_fund_accounts WHERE posale_id = POSALE.id AND currency_id = NEW.currency_id;
    				IF ( VAR_FUND_ACCOUNT_ID IS NULL ) THEN
    					VAR_FUND_ACCOUNT_ID := POSALE.fund_account_id;
    				END IF;
    			END IF;
    		END IF;

        		INSERT INTO fund_account_transactions (
        			fund_account_id,
        			curr_amount,
        			currency_id,
        			issue_date,
        			appl_date,
        			method,
        			user_id,
        			created,
        			credit_card,
        			credit_card_id,
      			payment_processor_id,
      			notes,
      			contact_id
        		)
        		VALUES (
        			VAR_FUND_ACCOUNT_ID,
        			ROUND(NEW.amount - VAR_COMISION - VAR_RET_IVA - VAR_RET_RENTA, _CURRENCY.decimals),
        			COALESCE(TR.currency_id, SALE.currency_id),
        			APPLDATE,
        			APPLDATE, -- REEMPLAZAR POR EL CALCULO
        			'sale',
        			SALE.user_id,
        			SALE.created,
        			PM.request_credit_card,
        			NEW.credit_card_id,
      			PPID,
      			V_NOTES,
      			SALE.contact_id
        		);

        		INSERT INTO fat_spm (
        			sales_payment_method_id,
        			fund_account_transaction_id
        		)
        		VALUES (
        			NEW.id,
        			( SELECT CURRVAL('fund_account_transactions_id_seq') )
        		);


        		-- SI EL METODO DE PAGO ES ES SENHA, ENTONCES SE MARCA LA SENHA COMO UTILIZADA
        		IF (TR.down_payment_id IS NOT NULL) THEN
        			UPDATE down_payments SET sale_id = TR.sale_id WHERE id = TR.down_payment_id;
        		END IF;
        	END IF;

        	IF ( TR.delayed = TRUE AND FALSE ) THEN
      		INSERT INTO sales_pnotes (
      			sale_id,
      			expiration_date,
      			note_number,
      			user_id,
      			amount,
      			sales_payment_method_id
      		)
      		SELECT
      			TR.sale_id,
      			SALE.issue_date,
      			'1',
      			SALE.user_id,
      			SALE.amount,
      			TR.id
      		WHERE
      			TR.id NOT IN ( SELECT sales_payment_method_id FROM sales_pnotes WHERE sales_payment_method_id IS NOT NULL AND sale_id = TR.sale_id )
      		;
        	END IF;
        	----------------------------------------------------------------

      	DELETE FROM sales_payment_methods_collections WHERE sales_payment_method_id = TR.id;
      	IF ( TR.advance_collection_id IS NOT NULL AND TG_OP <> 'DELETE' ) THEN
      		INSERT INTO sales_payment_methods_collections (
      			collection_id,
      			sales_payment_method_id,
      			amount_sale,
      			amount_collection
      		)
      		VALUES (
      			TR.advance_collection_id,
      			TR.id,
      			TR.amount,
      			TR.amount
      		);
      	END IF;

        	IF ( TR.advance = TRUE AND TR.advance_collection_id IS NULL AND TG_OP <> 'DELETE' ) THEN
        		SALE_AMOUNT := TR.amount;
        		FOR COLLECTION IN ( SELECT * FROM collections WHERE advance = TRUE AND advance_used < amount AND collections.contact_id = SALE.contact_id ) LOOP
        			IF (SALE_AMOUNT > 0) THEN
        				ADVANCE_AMOUNT := COLLECTION.amount - COLLECTION.advance_used;
        				IF ( ADVANCE_AMOUNT > SALE_AMOUNT ) THEN
        					RELATION_AMOUNT := SALE_AMOUNT;
        				ELSE
        					RELATION_AMOUNT := ADVANCE_AMOUNT;
        				END IF;
        				SALE_AMOUNT := SALE_AMOUNT - RELATION_AMOUNT;
        				INSERT INTO sales_payment_methods_collections ( collection_id, sales_payment_method_id, amount_sale, amount_collection ) VALUES ( COLLECTION.id, TR.id, RELATION_AMOUNT, RELATION_AMOUNT );
        			END IF;
        		END LOOP;
        	END IF;

        	IF ( TR.cv_id IS NOT NULL ) THEN
    		IF ( TR.id IS NOT NULL ) THEN
    			DELETE FROM
    				cvs_transactions
    			WHERE
    				sales_payment_method_id = TR.id
    			;
    		END IF;
    		IF (SALE.void = FALSE) THEN
    			INSERT INTO cvs_transactions (
    				cv_id,
    				amount,
    				user_id,
    				created,
    				sales_payment_method_id
    			)
    			VALUES (
    				TR.cv_id,
    				TR.amount * SALE.currency_price * -1,
    				COALESCE(TR.user_id, SALE.user_id),
    				TR.created,
    				TR.id
    			);
    		END IF;
        	END IF;

        	IF (TR.sale_credit_note_id IS NOT NULL) THEN
  		PERFORM set_sale_credit_notes_discount_applied(TR.sale_credit_note_id);
        	END IF;

        	-- raise notice 'credito after %',tr.credit;
        	RETURN TR;
        END
        $$;


